<include file="Public:header"/>	<div class="mainbox">		<div id="nav" class="mainnav_title">			<ul>				<if condition="!C('butt_open')">					<a href="{pigcms{:U('Adver/index')}">{pigcms{:L('I_ADCATEGORY_LIST')}</a>|					<a href="{pigcms{:U('Adver/adver_list',array('cat_id'=>$now_category['cat_id']))}" class="on">{pigcms{$now_category.cat_name} - {pigcms{:L('I_AD_LIST')}</a>|				<else/>					<a href="{pigcms{:U('Adver/adver_list',array('cat_id'=>$now_category['cat_id']))}" class="on">{pigcms{$now_category.cat_name}</a>|				</if>				<a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Adver/adver_add',array('cat_id'=>$now_category['cat_id']))}','{pigcms{:L(\'I_ADD_AD\')}',600,420,true,false,false,addbtn,'add',true);">{pigcms{:L('I_ADD_AD')}</a>			</ul>		</div>        <table class="search_table" width="100%">            <tr>                <td>                    <form action="" method="get">                        <input type="hidden" name="c" value="Adver"/>                        <input type="hidden" name="a" value="adver_list"/>                        <input type="hidden" name="cat_id" value="{pigcms{$now_category['cat_id']}"/>                        City:                        <select name="search_city">                            <option value="0" <if condition="$_GET['search_city'] eq '0'">selected="selected"</if>>All</option>                            <voList name="city_list" id="vo">                                <option value="{pigcms{$vo.area_id}" <if condition="$_GET['search_city'] eq $vo['area_id']">selected="selected"</if>>{pigcms{$vo.area_name}</option>                            </voList>                        </select>                        <input type="submit" value="{pigcms{:L('_BACK_SEARCH_')}" class="button"/>                    </form>                </td>            </tr>        </table>		<form name="myform" id="myform" action="" method="post">			<div class="table-list">				<table width="100%" cellspacing="0">					<colgroup>						<col/>						<col/>						<if condition="$many_city eq 1">							<col/>						</if>						<col/>						<col/>						<col width="180" align="center"/>						<col width="180" align="center"/>					</colgroup>					<thead>						<tr>							<th>{pigcms{:L('G_ID')}</th>							<th>{pigcms{:L('G_NAME')}</th>							<if condition="$many_city eq 1">								<th>{pigcms{:L('G_CITY')}</th>							</if>							<!--th>补齐</th-->							<th>{pigcms{:L('I_URL')}</th>							<th>{pigcms{:L('I_BANNER_PHO')}</th>							<th class="textcenter">{pigcms{:L('I_LAST_OPERATED')}</th>							<th>{pigcms{:L('G_STATUS')}</th>							<th class="textcenter">{pigcms{:L('G_ACTION')}</th>						</tr>					</thead>					<tbody>						<if condition="is_array($adver_list)">							<volist name="adver_list" id="vo">								<tr>									<td>{pigcms{$vo.id}</td>									<td>{pigcms{$vo.name}</td>									<if condition="$many_city eq 1">										<if condition="$vo['city_id'] eq '通用'">											<td style="color:red;">{pigcms{$vo.city_id}</td>										<else/>											<td>{pigcms{$vo.city_id}</td>										</if>									</if>									<!--td><if condition="$vo.complete eq 1"><span style="color:red;">Yes</span><else/>No</if></td-->									<td><a href="{pigcms{$vo.url}" target="_blank">{pigcms{:L('I_ACCESS_URL')}</a></td>									<td>										<img src="{pigcms{$config.site_url}/upload/adver/{pigcms{$vo.pic}" style="width:300px;height:80px;" class="view_msg"/>									</td>									<td class="textcenter">{pigcms{$vo.last_time|date='Y-m-d H:i:s',###}</td>									<td>									<if condition="$vo['status'] eq 1">										<font color="green">{pigcms{:L('B_STATACTIVE')}</font>									<elseif condition="$vo['status'] eq 0"/>										<font color="red">{pigcms{:L('B_STATCLD')}</font>									</if></td>									<td class="textcenter"><a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Adver/adver_edit',array('id'=>$vo['id'],'frame_show'=>true))}','{pigcms{:L(\'BASE_VIEW\')}',600,420,true,false,false,false,'add',true);">{pigcms{:L('BASE_VIEW')}</a> | <a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Adver/adver_edit',array('id'=>$vo['id']))}','{pigcms{:L(\'BASE_EDIT\')}',600,420,true,false,false,editbtn,'add',true);">{pigcms{:L('BASE_EDIT')}</a> | <a href="javascript:void(0);" class="delete_row" parameter="id={pigcms{$vo.id}" url="{pigcms{:U('Adver/adver_del')}">{pigcms{:L('BASE_DELETE')}</a></td>								</tr>							</volist>						<else/>							<tr><td class="textcenter red" colspan="8">{pigcms{:L('_BACK_EMPTY_')}</td></tr>						</if>					</tbody>				</table>			</div>		</form>	</div><include file="Public:footer"/>