<include file="Public:header"/>		<div class="mainbox">			<div id="nav" class="mainnav_title">				<ul>					<a href="{pigcms{:U('Adver/index')}" class="on">{pigcms{:L('I_ADCATEGORY_LIST')}</a>|					<a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Adver/cat_add')}','{pigcms{:L(\'I_ADD_CATEGORY\')}',400,180,true,false,false,addbtn,'add',true);">{pigcms{:L('I_ADD_CATEGORY')}</a>				</ul>			</div>			<form name="myform" id="myform" action="" method="post">				<div class="table-list">					<table width="100%" cellspacing="0">						<colgroup><col> <col> <col><col>  <col width="180" align="center"> </colgroup>						<thead>							<tr>								<th>{pigcms{:L('G_ID')}</th>								<th>{pigcms{:L('G_NAME')}</th>								<th>{pigcms{:L('I_SYMBOL')}</th>								<th>{pigcms{:L('I_TYPE')}</th>								<th>{pigcms{:L('I_AD_LIST')}</th>								<th class="textcenter">{pigcms{:L('E_ACTION')}</th>							</tr>						</thead>						<tbody>							<if condition="is_array($category_list)">								<volist name="category_list" id="vo">									<tr>										<td>{pigcms{$vo.cat_id}</td>										<td>{pigcms{$vo.cat_name}</td>										<td>{pigcms{$vo.cat_key}</td>										<td><if condition="$vo['cat_type']">PC<else />WAP</if></td>										<td><a href="{pigcms{:U('Adver/adver_list',array('cat_id'=>$vo['cat_id']))}">{pigcms{:L('I_AD_LIST')}</a></td>										<td class="textcenter"><a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Adver/cat_edit',array('cat_id'=>$vo['cat_id'],'frame_show'=>true))}','{pigcms{:L(\'BASE_VIEW\')}',400,180,true,false,false,false,'add',true);">{pigcms{:L('BASE_VIEW')}</a> | <a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Adver/cat_edit',array('cat_id'=>$vo['cat_id']))}','{pigcms{:L(\'BASE_EDIT\')}',400,180,true,false,false,editbtn,'add',true);">{pigcms{:L('BASE_EDIT')}</a> | <a href="javascript:void(0);" class="delete_row" parameter="cat_id={pigcms{$vo.cat_id}" url="{pigcms{:U('Adver/cat_del')}">{pigcms{:L('BASE_DELETE')}</a></td>									</tr>								</volist>							<else/>								<tr><td class="textcenter red" colspan="8">{pigcms{:L('_BACK_EMPTY_')}</td></tr>							</if>						</tbody>					</table>				</div>			</form>		</div><include file="Public:footer"/>