<include file="Public:header"/>		<div class="mainbox">			<div id="nav" class="mainnav_title">				<ul>					<a href="{pigcms{:U('Area/index')}" <if condition="$_GET['type'] eq 1">class="on"</if>>{pigcms{:L('I_PROVINCE_LIST')}</a>|					<a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Area/addadmin',array('area_id'=>$_GET['area_id']))}','{pigcms{:L(\'I_ADD_ADMIN\')}',450,320,true,false,false,addbtn,'add',true);">{pigcms{:L('I_ADD_ADMIN')}</a>				</ul>			</div>			<form name="myform" id="myform" action="" method="post">				<div class="table-list">					<table width="100%" cellspacing="0">						<colgroup>							<col/>							<col/>							<col/>							<col/>							<col/>							<col/>							<col/>							<col/>							<col/>						</colgroup>						<thead>							<tr>								<th>{pigcms{:L('I_ACCOUNT_NAME')}</th>								<th>{pigcms{:L('B_FULLNAME')}</th>								<th>{pigcms{:L('B_USERPHONE')}</th>								<th>{pigcms{:L('B_USERPEMAIL')}</th>								<!--th>QQ</th-->								<th>{pigcms{:L('B_LASTLOGIN')}</th>								<th>{pigcms{:L('B_NUMOFLOGIN')}</th>								<th>{pigcms{:L('B_USERSTAT')}</th>								<th>{pigcms{:L('B_ACTION')}</th>							</tr>						</thead>						<tbody>							<if condition="is_array($admin)">								<volist name="admin" id="vo">									<tr>										<td>{pigcms{$vo.account}</td>										<td>{pigcms{$vo.realname}</td>										<td>{pigcms{$vo.phone}</td>										<td>{pigcms{$vo.email}</td>										<!--td>{pigcms{$vo.qq}</td-->										<td>{pigcms{$vo.last_time|date="Y-m-d H:i:s",###}</td>										<td>{pigcms{$vo.login_count}</td>										<td><if condition="$vo['status'] eq 1"><span style="color:green">{pigcms{:L('B_STATACTIVE')}</span><else /><span style="color:red">{pigcms{:L('B_STATCLD')}</span></if></td>										<td>											<a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Area/addadmin',array('id'=>$vo['id'], 'area_id'=>$vo['area_id']))}','{pigcms{:L(\'BASE_EDIT\')}',450,320,true,false,false,editbtn,'add',true);">{pigcms{:L('BASE_EDIT')}</a>										</td>									</tr>								</volist>							<else/>								<tr><td class="textcenter red" colspan="8">{pigcms{:L('_BACK_EMPTY_')}</td></tr>							</if>						</tbody>					</table>				</div>			</form>		</div><include file="Public:footer"/>