<include file="Public:header"/>		<div class="mainbox">			<div id="nav" class="mainnav_title">				<ul>					<a href="{pigcms{:U('Area/index')}" <if condition="$_GET['type'] eq 1">class="on"</if>>{pigcms{:L('I_PROVINCE_LIST')}</a>|					<if condition="($config['now_province'] && $_GET['type'] gt 2) || ($config['many_city'] && !$config['now_province'] && $_GET['type'] gt 1)">						<a href="{pigcms{:U('Area/index',array('pid'=>$now_area['area_pid'],'type'=>$_GET['type']-1))}" <if condition="$_GET['type'] eq 1">class="on"</if>>上一级</a>|					</if>					<if condition="!$config['many_city'] && $_GET['type'] gt 4">						<a href="{pigcms{:U('Area/index',array('pid'=>$now_area['area_pid'],'type'=>$_GET['type']-1))}" <if condition="$_GET['type'] eq 1">class="on"</if>>上一级</a>|					</if>					<if condition="$_GET['type'] eq 2">						<a href="{pigcms{:U('Area/index',array('pid'=>$_GET['pid'],'type'=>$_GET['type']))}" class="on">                            {pigcms{:L('I_CITY_LIST')}                        </a>					</if>                    <if condition="$_GET['type'] eq 3">                        <a href="{pigcms{:U('Area/index',array('pid'=>$_GET['pid'],'type'=>$_GET['type']))}" class="on">                            Region List                        </a>                    </if>					<a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Area/add',array('pid'=>$_GET['pid'],'type'=>$_GET['type']))}','Add',450,320,true,false,false,addbtn,'add',true);">                        <if condition="$_GET['type'] eq 2">                            Add a City                        </if>                        <if condition="$_GET['type'] eq 3">                            Add a Region                        </if>                        <if condition="$_GET['type'] eq 1">                        {pigcms{:L('I_ADD_A_PROVINCE')}                        </if>                    </a>				</ul>			</div>			<form name="myform" id="myform" action="" method="post">				<div class="table-list">					<table width="100%" cellspacing="0">						<colgroup>							<col/>							<col/>							<col/>							<if condition="$_GET['type'] eq 2 || $_GET['type'] eq 4">								<col/>							</if>							<col/>							<if condition="$_GET['type'] gt 1">								<col/>								<if condition="$_GET['type'] lt 4">									<col/>								</if>							</if>							<if condition="$_GET['type'] lt 4">								<col/>							</if>							<col width="240" align="center"/>						</colgroup>						<thead>							<tr>								<th>{pigcms{:L('I_LISTING_ORDER')}</th>								<th>{pigcms{:L('G_ID')}</th>								<th>{pigcms{:L('G_NAME')}</th>								<if condition="$_GET['type'] eq 2 || $_GET['type'] eq 4">									<th>{pigcms{:L('I_INITIAL_LETTER')}</th>								</if>								<th>{pigcms{:L('G_STATUS')}</th>								<if condition="$_GET['type'] gt 1">									<th>{pigcms{:L('I_NETWOEK_SYMBOL')}</th>									<if condition="$_GET['type'] lt 4">										<th>{pigcms{:L('I_IP_SYMPOL')}</th>									</if>								</if>                                <!--if condition="$_GET['type'] eq 4">                                    <th>View</th>                                </if-->                                <if condition="$_GET['type'] eq 2">                                    <th>View Regions</th>                                </if>                                <if condition="$_GET['type'] eq 3">                                    <th>View Malls</th>                                </if>                                <if condition="$_GET['type'] eq 1">                                    <th>{pigcms{:L('I_VIEW_CITIES')}</th>                                </if>								<th>{pigcms{:L('E_ACTION')}</th>							</tr>						</thead>						<tbody>							<if condition="is_array($area_list)">								<volist name="area_list" id="vo">									<tr>										<td>{pigcms{$vo.area_sort}</td>										<td>{pigcms{$vo.area_id}</td>										<td><if condition="$vo['is_hot']"><font color="red">{pigcms{$vo.area_name}</font><else/>{pigcms{$vo.area_name}</if></td>										<if condition="$_GET['type'] eq 2 || $_GET['type'] eq 4">											<td>{pigcms{$vo.first_pinyin}</td>										</if>										<td><if condition="$vo['is_open']"><font color="green">{pigcms{:L('I_ACTIVE')}</font><else/><font color="red">{pigcms{:L('_BACK_FORBID_')}</font></if></td>										<if condition="$_GET['type'] gt 1">											<td>{pigcms{$vo.area_url}</td>											<if condition="$_GET['type'] lt 4">												<td>{pigcms{$vo.area_ip_desc}</td>											</if>										</if>										<if condition="$_GET['type'] lt 4">											<td>                                                <a href="{pigcms{:U('Area/index',array('type'=>$_GET['type']+1,'pid'=>$vo['area_id']))}">                                                    <if condition="$_GET['type'] eq 2">                                                        View Regions                                                    </if>                                                    <if condition="$_GET['type'] eq 3">                                                        View Malls                                                    </if>                                                    <if condition="$_GET['type'] eq 1">                                                        {pigcms{:L('I_VIEW_CITIES')}                                                    </if>                                                </a>                                            </td>										</if>										<!--if condition="$_GET['type'] eq 4">											<td><a href="{pigcms{:U('Area/area_market',array('pid'=>$vo['area_id']))}">View</a></td>										</if-->										<td>											<a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Area/edit',array('area_id'=>$vo['area_id']))}','{pigcms{:L(\'BASE_EDIT\')}',450,320,true,false,false,editbtn,'add',true);">{pigcms{:L('BASE_EDIT')}</a> |											<a href="javascript:void(0);" class="delete_row" parameter="area_id={pigcms{$vo.area_id}" url="{pigcms{:U('Area/del')}">{pigcms{:L('E_DELETE')}</a>											<if condition="$_GET['type'] eq 2">											 | <a href="{pigcms{:U('Area/admin', array('area_id' => $vo['area_id']))}">{pigcms{:L('B_CADMIN')}</a>											 <php>if($system_session['area_id']==$vo['area_id']||$is_system){</php>											 <!--| <a href="{pigcms{:U('Bill/merchant_percentage', array('city_id' => $vo['area_id']))}">抽成记录列表</a>-->											 <php>}</php>											<elseif condition="$_GET['type'] eq 3" />											 | <a href="{pigcms{:U('Area/admin', array('area_id' => $vo['area_id']))}">区域管理员</a>											  <php>if($system_session['area_id']==$vo['area_id']||$is_system){</php>											 <!--| <a href="{pigcms{:U('Bill/merchant_percentage', array('area_id' => $vo['area_id']))}">抽成记录列表</a>-->											 											  <php>}</php>											</if>										</td>									</tr>								</volist>							<else/>								<tr><td class="textcenter red" colspan="8">列表为空！</td></tr>							</if>						</tbody>					</table>				</div>			</form>		</div><include file="Public:footer"/>