<include file="Public:header"/>		<div class="mainbox">			<div id="nav" class="mainnav_title">				<ul>					<a href="{pigcms{:U('Event/index')}" class="on">{pigcms{:L('G_EVENT_LIST')}</a>|					<a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Event/add_coupon',array('event_id'=>$event['id']))}','{pigcms{:L(\'G_ADD_COUPON\')}',480,300,true,false,false,addbtn,'add',true);">{pigcms{:L('G_ADD_COUPON')}</a>|				</ul>			</div>			<form name="myform" id="myform" action="" method="post">                <div style="font-size: 14px;padding: 5px 2px;">{pigcms{$event.name}</div>				<div class="table-list">					<table width="100%" cellspacing="0">						<colgroup>							<col/>							<col/>							<col/>							<col/>							<col/>							<col/>							<if condition="$system_session['level'] eq 2 || $system_session['level'] eq 0">								<col width="180" align="center"/>							</if>						</colgroup>						<thead>							<tr>								<th>{pigcms{:L('G_ID')}</th>								<th>{pigcms{:L('G_NAME')}</th>								<th>{pigcms{:L('G_MIN_ORDER')}</th>								<th>{pigcms{:L('G_DISCOUNT_AMOUNT')}</th>								<th>                                    {pigcms{$event['type_name']}                                </th>                                <th>{pigcms{:L('G_NUMBER_CLAIMS')}</th>                                <th>{pigcms{:L('G_NUMBER_USED')}</th>                                <th>{pigcms{:L('G_EXPIRED_AMOUNT')}</th>                                <th>                                    <if condition="$event['type'] eq 3 or $event['type'] eq 4 or $event['type'] eq 5">                                        {pigcms{:L('G_COMBINED')}                                        <else />                                        {pigcms{:L('G_COUP_TYPE')}                                    </if>                                </th>								<if condition="$system_session['level'] eq 2 || $system_session['level'] eq 0">									<th class="textcenter">{pigcms{:L('G_ACTION')}</th>								</if>							</tr>						</thead>						<tbody>							<if condition="is_array($coupon_list)">								<volist name="coupon_list" id="vo">									<tr>										<td>{pigcms{$vo.id}</td>										<td>{pigcms{$vo.name}</td>										<td>{pigcms{$vo.use_price}</td>                                        <td>{pigcms{$vo.discount}</td>                                        <if condition="$vo['store_name']">                                            <td>{pigcms{$vo.store_name}</td>                                            <else />                                            <td>{pigcms{$vo.limit_day}</td>                                        </if>                                        <td>{pigcms{$vo.all_num}</td>                                        <td>{pigcms{$vo.use_num}</td>                                        <td>{pigcms{$vo.expiry_num}</td>                                        <td>                                            <if condition="$vo['type'] eq 0">                                                <if condition="$event['type'] eq 3 or $event['type'] eq 4 or $event['type'] eq 5">                                                    {pigcms{:L('G_NO')}                                                    <else />                                                    {pigcms{:L('G_INVITEE')}                                                </if>                                                <else />                                                <if condition="$event['type'] eq 3 or $event['type'] eq 4 or $event['type'] eq 5">                                                    {pigcms{:L('G_YES')}                                                    <else />                                                    {pigcms{:L('G_INVITER')}                                                </if>                                            </if>                                        </td>										<if condition="$system_session['level'] eq 2 || $system_session['level'] eq 0">											<td class="textcenter">                                                <a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Event/edit_coupon',array('id'=>$vo['id']))}','{pigcms{:L(\'G_EDIT\')}',480,370,true,false,false,editbtn,'add',true);">{pigcms{:L('G_EDIT')}</a>                                                <if condition="$event['type'] eq 4 or $event['type'] eq 5">                                                    | <a href="javascript:void(0);" class="delete_row" parameter="id={pigcms{$vo.id}" url="{pigcms{:U('Event/coupon_del')}">{pigcms{:L('QW_DELETE')}</a></td>                                                </if>										</if>									</tr>								</volist>							<else/>								<tr><td class="textcenter red" colspan="10">{pigcms{:L('_BACK_EMPTY_')}</td></tr>							</if>						</tbody>					</table>				</div>			</form>		</div><include file="Public:footer"/>