<include file="Public:header"/>		<div class="mainbox">			<div id="nav" class="mainnav_title">				<ul>					<a href="{pigcms{:U('Event/index')}" class="on">活动列表</a>|					<a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Event/add_coupon',array('event_id'=>$event['id']))}','创建优惠券',480,300,true,false,false,addbtn,'add',true);">创建优惠券</a>|				</ul>			</div>			<form name="myform" id="myform" action="" method="post">                <div style="font-size: 14px;padding: 5px 2px;">{pigcms{$event.name}</div>				<div class="table-list">					<table width="100%" cellspacing="0">						<colgroup>							<col/>							<col/>							<col/>							<col/>							<col/>							<col/>							<if condition="$system_session['level'] eq 2 || $system_session['level'] eq 0">								<col width="180" align="center"/>							</if>						</colgroup>						<thead>							<tr>								<th>编号</th>								<th>名称</th>								<th>消费金额</th>								<th>优惠金额</th>								<th>                                    {pigcms{$event['type_name']}                                </th>                                <th>领取数</th>                                <th>使用数</th>                                <th>过期数</th>                                <th>                                    <if condition="$event['type'] eq 3 or $event['type'] eq 4 or $event['type'] eq 5">                                        可否使用优惠券                                        <else />                                        优惠券类型                                    </if>                                </th>								<if condition="$system_session['level'] eq 2 || $system_session['level'] eq 0">									<th class="textcenter">操作</th>								</if>							</tr>						</thead>						<tbody>							<if condition="is_array($coupon_list)">								<volist name="coupon_list" id="vo">									<tr>										<td>{pigcms{$vo.id}</td>										<td>{pigcms{$vo.name}</td>										<td>{pigcms{$vo.use_price}</td>                                        <td>{pigcms{$vo.discount}</td>                                        <if condition="$vo['store_name']">                                            <td>{pigcms{$vo.store_name}</td>                                            <else />                                            <td>{pigcms{$vo.limit_day}</td>                                        </if>                                        <td>{pigcms{$vo.all_num}</td>                                        <td>{pigcms{$vo.use_num}</td>                                        <td>{pigcms{$vo.expiry_num}</td>                                        <td>                                            <if condition="$vo['type'] eq 0">                                                <if condition="$event['type'] eq 3 or $event['type'] eq 4 or $event['type'] eq 5">                                                    不可                                                    <else />                                                    本人                                                </if>                                                <else />                                                <if condition="$event['type'] eq 3 or $event['type'] eq 4 or $event['type'] eq 5">                                                    可用                                                    <else />                                                    邀请人                                                </if>                                            </if>                                        </td>										<if condition="$system_session['level'] eq 2 || $system_session['level'] eq 0">											<td class="textcenter">                                                <a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Event/edit_coupon',array('id'=>$vo['id']))}','编辑活动分类',480,370,true,false,false,editbtn,'add',true);">编辑</a>                                                <if condition="$event['type'] eq 4 or $event['type'] eq 5">                                                    | <a href="javascript:void(0);" class="delete_row" parameter="id={pigcms{$vo.id}" url="{pigcms{:U('Event/coupon_del')}">删除</a></td>                                                </if>										</if>									</tr>								</volist>							<else/>								<tr><td class="textcenter red" colspan="8">列表为空！</td></tr>							</if>						</tbody>					</table>				</div>			</form>		</div><include file="Public:footer"/>