<include file="Public:header"/>		<div class="mainbox">			<div id="nav" class="mainnav_title">				<ul>					<a href="{pigcms{:U('Event/index')}" class="on">{pigcms{:L('G_EVENT_LIST')}</a>|					<a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Event/add')}','{pigcms{:L(\'G_ADD_EVENT\')}',480,300,true,false,false,addbtn,'add',true);">{pigcms{:L('G_ADD_EVENT')}</a>|				</ul>			</div>			<form name="myform" id="myform" action="" method="post">				<div class="table-list">					<table width="100%" cellspacing="0">						<colgroup>							<col/>							<col/>							<col/>							<col/>							<col/>							<col/>							<if condition="$system_session['level'] eq 2 || $system_session['level'] eq 0">								<col width="180" align="center"/>							</if>						</colgroup>						<thead>							<tr>								<th>{pigcms{:L('G_ID')}</th>								<th>{pigcms{:L('G_NAME')}</th>								<th>{pigcms{:L('G_TYPE')}</th>								<th>{pigcms{:L('G_START_TIME')}</th>								<th>{pigcms{:L('G_END_TIME')}</th>                                <th>{pigcms{:L('G_STATUS')}</th>                                <th>{pigcms{:L('G_CITY')}</th>								<th>{pigcms{:L('G_COUPON_LIST')}</th>								<if condition="$system_session['level'] eq 2 || $system_session['level'] eq 0">									<th class="textcenter">{pigcms{:L('G_ACTION')}</th>								</if>							</tr>						</thead>						<tbody>							<if condition="is_array($event_list)">								<volist name="event_list" id="vo">									<tr>										<td>{pigcms{$vo.id}</td>										<td>{pigcms{$vo.name}</td>										<td>{pigcms{$vo.type_name}</td>										<td>                                            <if condition="$vo['begin_time'] eq 0">                                                {pigcms{:L('G_UNLIMITED')}                                            <else />                                                {pigcms{$vo.begin_time|date='Y-m-d',###}                                            </if>                                        </td>										<td>                                            <if condition="$vo['end_time'] eq 0">                                                {pigcms{:L('G_UNLIMITED')}                                                <else />                                                {pigcms{$vo.end_time|date='Y-m-d',###}                                            </if>                                        </td>                                        <td>                                            <if condition="$vo['status'] eq 1">                                                <label style="color: green">{pigcms{$vo.status_name}</label>                                                <else />                                                {pigcms{$vo.status_name}                                            </if>                                        </td>                                        <td>                                            <if condition="$vo['type'] neq 3">                                                -                                            <else />                                                {pigcms{$vo.city_name}                                            </if>                                        </td>										<td><a href="{pigcms{:U('Event/coupon_list',array('id'=>$vo['id']))}">{pigcms{:L('G_COUPON_LIST')}</a></td>										<if condition="$system_session['level'] eq 2 || $system_session['level'] eq 0">											<td class="textcenter"><a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Event/edit',array('id'=>$vo['id']))}','{pigcms{:L(\'G_EDIT\')}',480,370,true,false,false,editbtn,'add',true);">{pigcms{:L('G_EDIT')}</a> <!--| <a href="javascript:void(0);" class="delete_row" parameter="id={pigcms{$vo.activity_id}" url="{pigcms{:U('Activity/del')}">删除</a--></td>										</if>									</tr>								</volist>							<else/>								<tr><td class="textcenter red" colspan="9">{pigcms{:L('_BACK_EMPTY_')}</td></tr>							</if>						</tbody>					</table>				</div>			</form>		</div><include file="Public:footer"/>