<include file="Public:header"/>		<div class="mainbox">			<div id="nav" class="mainnav_title">				<ul>					<a href="{pigcms{:U('Index/account')}" class="on">{pigcms{:L('B_USERLIST')}</a>|					<a href="javascript:void(0);"  onclick="window.top.artiframe('{pigcms{:U('Index/admin',array('area_id'=>$_GET['area_id']))}','{pigcms{:L(\'B_ADDUSER\')}',450,320,true,false,false,addbtn,'add',true);">{pigcms{:L('B_ADDUSER')}</a>				</ul>			</div>			<form name="myform" id="myform" action="" method="post">				<div class="table-list">					<table width="100%" cellspacing="0">						<thead>							<tr>								<th>{pigcms{:L('B_ACCOUNTID')}</th>								<th>{pigcms{:L('B_ACCOUNTNAME')}</th>								<th>{pigcms{:L('B_USERWECHAT')}</th>								<th>{pigcms{:L('B_NAME')}</th>								<th>{pigcms{:L('B_USERPHONE')}</th>								<th>{pigcms{:L('B_USERPEMAIL')}</th>								<th>{pigcms{:L('B_LASTLOGIN')}</th>                                <th>{pigcms{:L('BASE_CITY')}</th>								<th class="textcenter">{pigcms{:L('B_NUMOFLOGIN')}</th>								<th class="textcenter">{pigcms{:L('B_USERSTAT')}</th>								<th class="textcenter">{pigcms{:L('B_ACTION')}</th>							</tr>						</thead>						<tbody>							<if condition="is_array($admins)">								<volist name="admins" id="vo">									<tr>										<td>{pigcms{$vo.id}</td>										<td>{pigcms{$vo.account}</td>										<td class="nickname">											<if condition="empty($vo['openid'])">                                            <a href="{pigcms{$config.site_url}/index.php?g=Index&c=Recognition&a=see_tmp_qrcode&qrcode_id={pigcms{$vo['id'] + 3890000000}&img=1" data-id="{pigcms{$vo['id']}" class="see_qrcode" id="qr_{pigcms{$vo['id']}" style="color:green">{pigcms{:L('B_LINKWECHAT')}</a>                                            <else/>                                            {pigcms{$vo.nickname}                                            </if>                                         </td>										<td>{pigcms{$vo.realname}</td>										<td>{pigcms{$vo.phone}</td>										<td>{pigcms{$vo.email}</td>										<td><if condition="$vo['last_time']">{pigcms{$vo.last_time|date='Y-m-d H:i:s',###}<else/>无</if></td>										<td>{pigcms{$vo.city_name}</td>                                        <td class="textcenter">{pigcms{$vo.login_count}</td>										<td class="textcenter"><if condition="$vo['status'] eq 1"><span style="color:green">{pigcms{:L('B_STATACTIVE')}</span><else /><span style="color:red">{pigcms{:L('B_STATCLD')}</span></if></td>										<td class="textcenter">										<if condition="$vo['level'] neq 2">										<a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Index/admin',array('id'=>$vo['id']))}','{pigcms{:L(\'BASE_EDIT\')}',450,320,true,false,false,editbtn,'edit',true);">{pigcms{:L('BASE_EDIT')}</a> |										<a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Index/menu',array('admin_id'=>$vo['id']))}','{pigcms{:L(\'B_PERMISSIONS\')}',800,500,true,false,false,editbtn,'edit',true);">{pigcms{:L('B_PERMISSIONS')}</a>										<if condition="$_SESSION['system']['level'] eq 2">| <a href="javascript:void(0);" class="delete_row" parameter="id={pigcms{$vo.id}" url="{pigcms{:U('account_del')}">{pigcms{:L('BASE_DELETE')}</a></if>										</if>										<if condition="$_SESSION['system']['level'] eq 2">										| <a class="label label-warning cancel" title="{pigcms{:L('B_CANCELLINK')}" data-id="{pigcms{$vo['id']}" style="<if condition="empty($vo['openid'])">display:none;</if>color:red" >{pigcms{:L('B_CANCELLINK')}</a>										</if>										</td>									</tr>								</volist>								<tr><td class="textcenter pagebar" colspan="11">{pigcms{$pagebar}</td></tr>                            <else/>                            <tr><td class="textcenter red" colspan="11">{pigcms{:L('_BACK_EMPTY_')}</td></tr>                            </if>						</tbody>					</table>				</div>			</form>		</div><script type="text/javascript" src="./static/js/artdialog/jquery.artDialog.js"></script><script type="text/javascript" src="./static/js/artdialog/iframeTools.js"></script><script type="text/javascript">var test;$(document).ready(function(){	$('.see_qrcode').live('click', function(){		test = art.dialog.open($(this).attr('href'),{			init: function(){				var iframe = this.iframe.contentWindow;				window.top.art.dialog.data('iframe_handle',iframe);			},			id: 'handle',			title:'扫描二维码绑定微信号',			padding: 0,			width: 430,			height: 433,			lock: true,			resize: false,			background:'black',			button: null,			fixed: false,			close: function(){clearInterval(t);},			left: '50%',			top: '38.2%',			opacity:'0.4'		});		var id = $(this).attr('data-id'), obj = $(this);	 	var t = window.setInterval(function(){			$.get("{pigcms{:U('Index/check_account')}", {id:id},  function(result){				if (result.error_code == 0) {					test.close();					clearInterval(t);					obj.parent('td').html(result.nickname).siblings('td').children('.cancel').show();				}			}, 'json');		},3000);		return false;	});	$('.cancel').click(function(){		var id = $(this).attr('data-id'), obj = $(this);		obj.attr('disabled', true);		$.get("{pigcms{:U('Index/cancel_account')}", {id:id}, function(result){			obj.attr('disabled', false);			if (result.error_code == 1) {				alert(result.msg);			} else {				var qrcode_id = 3890000000 + id;				obj.hide().parent('td').siblings('.nickname').html('<a href="{pigcms{$config.site_url}/index.php?g=Index&c=Recognition&a=see_tmp_qrcode&qrcode_id=' + qrcode_id + '&img=1" data-id="' + id + '" class="see_qrcode" style="color:green">绑定微信号</a>');			}		}, 'json');	});});</script><include file="Public:footer"/>