<include file="Public:header"/>		<div class="mainbox">			<div id="nav" class="mainnav_title">				<ul>					<a href="{pigcms{:U('Merchant/index')}">{pigcms{:L('_BACK_MER_LIST_')}</a>|					<a href="{pigcms{:U('Merchant/store',array('mer_id'=>$merchant['mer_id']))}" class="on">{pigcms{$merchant.name} - {pigcms{:L('_BACK_STORE_LIST_')}</a>                    <if condition="$system_session['level'] neq 3">                    |<a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Merchant/store_add',array('mer_id'=>$merchant['mer_id']))}','添加店铺',620,480,true,false,false,addbtn,'store_add',true);">添加店铺</a>                    </if>				</ul>			</div>			<form name="myform" id="myform" action="" method="post">				<div class="table-list">					<table width="100%" cellspacing="0">						<colgroup><col> <col> <col><col><col><col><col><col><col width="180" align="center"> </colgroup>						<thead>							<tr>								<th>{pigcms{:L('_BACK_CODE_')}</th>								<th>{pigcms{:L('_BACK_STORE_NAME_')}</th>								<th>{pigcms{:L('_BACK_STORE_PHONE_')}</th>								<th>{pigcms{:L('_BACK_LAST_EDIT_')}</th>								<th>{pigcms{:L('_BACK_PLAT_CLICK_')}</th>								<th>{pigcms{:L('_BACK_DINE_')}</th>								<th>{pigcms{:L('_BACK_LUNCH_')}</th>								<th>{pigcms{:L('_BACK_DELIVERY_')}</th>								<if condition="$config['store_open_waimai']"><th>{pigcms{$config.waimai_alias_name}</th></if>								<th>{pigcms{:L('_BACK_STATUS_')}</th>								<th class="textcenter">{pigcms{:L('_BACK_CZ_')}</th>							</tr>						</thead>						<tbody>							<if condition="is_array($store_list)">								<volist name="store_list" id="vo">									<tr>										<td>{pigcms{$vo.store_id}</td>										<td>{pigcms{$vo.name}</td>										<td>{pigcms{$vo.phone}</td>										<td><if condition="$vo['last_time']">{pigcms{$vo.last_time|date='Y-m-d H:i:s',###}<else/>无</if></td>										<td>{pigcms{$vo.hits}</td>										<td><if condition="$vo['have_meal'] eq 1"><font color="green">{pigcms{:L('_BACK_ON_')}</font><else/><font color="red">{pigcms{:L('_BACK_OFF_')}</font></if></td>										<td><if condition="$vo['have_group'] eq 1"><font color="green">{pigcms{:L('_BACK_ON_')}</font><else/><font color="red">{pigcms{:L('_BACK_OFF_')}</font></if></td>										<td><if condition="$vo['have_shop'] eq 1"><font color="green">{pigcms{:L('_BACK_ON_')}</font><else/><font color="red">{pigcms{:L('_BACK_OFF_')}</font></if></td>										<if condition="$config['store_open_waimai']"><td><if condition="$vo['have_waimai'] eq 1"><font color="green">{pigcms{:L('_BACK_ON_')}</font><else/><font color="red">{pigcms{:L('_BACK_OFF_')}</font></if></td></if>										<td><if condition="$vo['status'] eq 1"><font color="green">{pigcms{:L('_BACK_ACTIVE_')}</font><elseif condition="$vo['status'] eq 2"/><font color="red">{pigcms{:L('_BACK_PENDING_')}</font><else/><font color="red">{pigcms{:L('_BACK_CLOSED_')}</font></if></td>										<td class="textcenter">                                            <a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Merchant/store_edit',array('store_id'=>$vo['store_id'],'frame_show'=>true))}','{pigcms{:L(\'_BACK_VIEW_\')}',620,480,true,false,false,false,'detail',true);">{pigcms{:L('_BACK_VIEW_')}</a> | <a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Merchant/store_edit',array('store_id'=>$vo['store_id']))}','{pigcms{:L(\'_BACK_EDIT_STORE_INFO_\')}',620,480,true,false,false,editbtn,'store_add',true);">{pigcms{:L('_BACK_EDIT_')}</a> | <a href="javascript:void(0);" class="delete_row" parameter="store_id={pigcms{$vo.store_id}" url="{pigcms{:U('Merchant/store_del')}">{pigcms{:L('_BACK_DEL_')}</a></td>									</tr>								</volist>								<tr><td class="textcenter pagebar" colspan="11">{pigcms{$pagebar}</td></tr>							<else/>								<tr><td class="textcenter red" colspan="10">{pigcms{:L('_BACK_EMPTY_')}</td></tr>							</if>						</tbody>					</table>				</div>			</form>		</div><include file="Public:footer"/>