<include file="Public:header"/>		<div class="mainbox">			<div id="nav" class="mainnav_title">				<ul>										<a href="{pigcms{:U('Shop/index')}" <if condition="!$category">class="on"</if>>{pigcms{:L('C_CATEGORYLIST')}</a>|					<if condition="$category">					<a href="{pigcms{:U('Shop/index',array('parentid'=>$category['cat_id']))}" class="on">{pigcms{$category.cat_name} - {pigcms{:L('C_SUBCATLIST')}</a>|					</if>					<a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Shop/cat_add', array('parentid' => $parentid))}','{pigcms{:L(\'C_ADDCATEGORY\')}',480,360,true,false,false,addbtn,'add',true);"><if condition="$category">{pigcms{:L('C_ADDSUBCATE')}<else />{pigcms{:L('C_ADDCATEGORY')}</if></a>				</ul>			</div>            <table class="search_table" width="100%">                <tr>                    <td>                        <if condition="$system_session['level'] neq 3 and $parentid eq 0">                            City:                            <select name="searchtype" id="city_select">                                <option value="0" <if condition="$city_id eq '' or $city_id eq 0">selected="selected"</if>>All</option>                                <volist name="city" id="vo">                                    <option value="{pigcms{$vo.area_id}" <if condition="$city_id eq $vo['area_id']">selected="selected"</if>>{pigcms{$vo.area_name}</option>                                </volist>                            </select>                        </if>                    </td>                </tr>            </table>			<form name="myform" id="myform" action="" method="post">				<div class="table-list">					<table width="100%" cellspacing="0">						<colgroup>							<col/>							<col/>							<col/>							<col/>							<col/>							<col width="180" align="center"/>						</colgroup>						<thead>							<tr>								<th>{pigcms{:L('C_CATEGORYID')}</th>								<th>{pigcms{:L('C_CATEGORYORDER')}</th>								<th>{pigcms{:L('C_CATEGORYNAME')}</th>								<th>{pigcms{:L('C_CATEGORYURL')}</th>								<if condition="empty($parentid)">									<th>{pigcms{:L('C_VIEWSUB')}</th>								<else/>									<!--th>表单填写项</th-->								</if>								<th>{pigcms{:L('C_CATEGORYSTAT')}</th>                                <th>{pigcms{:L('BASE_TYPE')}</th>                                <th>{pigcms{:L('BASE_CITY')}</th>                                <th>{pigcms{:L('C_CATEGORYNUM')}</th>								<th>{pigcms{:L('C_CATEGORYSCS')}</th>								<th class="textcenter">{pigcms{:L('B_ACTION')}</th>							</tr>						</thead>						<tbody>							<if condition="is_array($category_list)">								<volist name="category_list" id="vo">									<tr>										<td>{pigcms{$vo.cat_id}</td>										<td>{pigcms{$vo.cat_sort}</td>										<td>{pigcms{$vo.cat_name}</td>										<td>{pigcms{$vo.cat_url}</td>										<if condition="empty($parentid)">											<td><a href="{pigcms{:U('Shop/index',array('parentid'=>$vo['cat_id']))}">{pigcms{:L('C_VIEWSUB')}</a></td>										<else/>											<!--td><a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('cue_field',array('cat_id'=>$vo['cat_id']))}','表单填写项',580,420,true,false,false,false,'detail',true);">表单填写项</a></td-->										</if>										<td><if condition="$vo['cat_status'] eq 1"><font color="green">{pigcms{:L('C_CATEGORYSEN')}</font><elseif condition="$vo['cat_status'] eq 2"/><font color="red">待审核</font><else/><font color="red">{pigcms{:L('C_CATEGORYDIS')}</font></if></td>                                        <td>                                            <if condition="$vo['cat_type'] eq 0">                                                <font color="green">{pigcms{:L('C_CATEGORYNOR')}</font>                                            <else/>                                                <font color="red">{pigcms{:L('C_CATEGORYFT')}</font>                                            </if>                                        </td>                                        <td>                                            <if condition="$vo['city_id'] eq 0">                                                <font color="gray">{pigcms{$vo['city_name']}</font>                                                <else/>                                                <font color="green">{pigcms{$vo['city_name']}</font>                                            </if>                                        </td>                                        <td>                                            <a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Shop/cat_store',array('cat_id'=>$vo['cat_id'], 'parentid'=>$vo['cat_fid']))}','{pigcms{:L(\'J_CATEGORIZED_STORES\')}',480,360,true,false,false,editbtn,'edit',true);">                                                {pigcms{$vo.store_num}                                            </a>                                        </td>                                        <td><if condition="$vo['show_method'] eq 0"><font color="green">{pigcms{:L('C_CATEGORYSCS1')}</font><elseif condition="$vo['show_method'] eq 1"/><font color="red">{pigcms{:L('C_CATEGORYSCS2')}</font><else/><font color="red">{pigcms{:L('C_CATEGORYSCS3')}</font></if></td>										<td class="textcenter">                                            <a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Shop/cat_edit',array('cat_id'=>$vo['cat_id'],'frame_show'=>true))}','{pigcms{:L(\'BASE_VIEW\')}',480,360,true,false,false,false,'detail',true);">{pigcms{:L('BASE_VIEW')}</a> |                                            <a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Shop/cat_edit',array('cat_id'=>$vo['cat_id'], 'parentid'=>$vo['cat_fid']))}','{pigcms{:L(\'BASE_EDIT\')}',480,360,true,false,false,editbtn,'edit',true);">{pigcms{:L('BASE_EDIT')}</a> |                                            <a href="javascript:void(0);" class="delete_row" parameter="cat_id={pigcms{$vo.cat_id}" url="{pigcms{:U('Shop/cat_del')}">{pigcms{:L('BASE_DELETE')}</a> |                                            <a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Shop/cat_service',array('cat_id'=>$vo['cat_id'], 'parentid'=>$vo['cat_fid']))}','{pigcms{:L(\'C_CATESF\')}',480,260,true,false,false,editbtn,'edit',true);">{pigcms{:L('BASE_SERVICEFEE')}</a> |                                            <a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Shop/cat_pay',array('cat_id'=>$vo['cat_id'], 'parentid'=>$vo['cat_fid']))}','{pigcms{:L(\'C_CATE3DS\')}',480,260,true,false,false,editbtn,'edit',true);">{pigcms{:L('BASE_ENCRYPTION')}</a>                                        </td>									</tr>								</volist>							<else/>								<tr><td class="textcenter red" colspan="10">{pigcms{:L('_BACK_EMPTY_')}</td></tr>							</if>						</tbody>					</table>				</div>			</form>		</div><script>    var city_id = $('#city_select').val();    $('#city_select').change(function () {        city_id = $(this).val();        window.location.href = "{pigcms{:U('Shop/index', $_GET)}" + "&city_id="+city_id;    });</script><include file="Public:footer"/>