<include file="Public:header"/><div id="wrapper">    <include file="Public:left_menu"/>    <!----------------------------------------    以上不要写代码     ------------------------------------------------>    <div class="row wrapper border-bottom white-bg page-heading">        <div class="col-lg-9">            <h2>{pigcms{:L('I_ADCATEGORY_LIST')}</h2>            <ol class="breadcrumb">                <li class="breadcrumb-item">                    <a href="{pigcms{:U('Index/index')}">Home</a>                </li>                <!--                <li class="breadcrumb-item">-->                <!--                    <a>UI Elements</a>-->                <!--                </li>-->                <li class="breadcrumb-item active">                    <strong>{pigcms{:L('I_ADCATEGORY_LIST')}</strong>                </li>            </ol>        </div>        <div class="col-lg-3" style="height 90px;margin-top:40px;">            <a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Adver/adver_add',array('cat_id'=>$now_category['cat_id']))}','{pigcms{:L(\'I_ADD_AD\')}',600,420,true,false,false,addbtn,'add',true);"><button type="button" class="btn btn-primary btn-sm float-right">{pigcms{:L('I_ADD_AD')}</button></a>        </div>    </div>    <div class="wrapper wrapper-content animated fadeInRight">        <div class="row">            <div class="col-lg-12">                <div class="ibox ">                    <div class="ibox-title">                        <h5>{pigcms{:L('I_ADCATEGORY_LIST')}</h5>                        <div class="ibox-tools">                        </div>                    </div>                    <div class="ibox-content">                        <div class="table-responsive">                            <!-------------------------------- 工具条 -------------------------------------->                            <div style="height: 50px;">                                <div id="tool_bar" class="form-inline tutti_toolbar">                                    <form action="" method="get">                                        <input type="hidden" name="c" value="Adver"/>                                        <input type="hidden" name="a" value="adver_list"/>                                        <input type="hidden" name="cat_id" value="{pigcms{$now_category['cat_id']}"/>                                        City:                                        <select name="search_city" class="form-control">                                            <option value="0" <if condition="$_GET['search_city'] eq '0'">selected="selected"</if>>All</option>                                            <voList name="city_list" id="vo">                                                <option value="{pigcms{$vo.area_id}" <if condition="$_GET['search_city'] eq $vo['area_id']">selected="selected"</if>>{pigcms{$vo.area_name}</option>                                            </voList>                                        </select>                                        <input type="submit" value="{pigcms{:L('_BACK_SEARCH_')}" class="form-control"/>                                    </form>                                </div>                            </div>                        </div>                        <table class="table table-striped table-bordered table-hover dataTables-example">                            <thead>                            <tr>                                <th>{pigcms{:L('G_ID')}</th>                                <th>{pigcms{:L('I_LISTING_ORDER2')}</th>                                <th>{pigcms{:L('G_NAME')}</th>                                <if condition="$many_city eq 1">                                    <th>{pigcms{:L('G_CITY')}</th>                                </if>                                <th>{pigcms{:L('I_URL')}</th>                                <th>{pigcms{:L('I_BANNER_PHO')}</th>                                <th>City</th>                                <th class="textcenter">{pigcms{:L('I_LAST_OPERATED')}</th>                                <th>{pigcms{:L('G_STATUS')}</th>                                <th class="textcenter">{pigcms{:L('G_ACTION')}</th>                            </tr>                            </thead>                            <tbody>                            <if condition="is_array($adver_list)">                                <volist name="adver_list" id="vo">                                    <tr>                                        <td>{pigcms{$vo.id}</td>                                        <td>{pigcms{$vo.sort}</td>                                        <td>{pigcms{$vo.name}</td>                                        <if condition="$many_city eq 1">                                            <if condition="$vo['city_id'] eq '通用'">                                                <td style="color:red;">{pigcms{$vo.city_id}</td>                                                <else/>                                                <td>{pigcms{$vo.city_id}</td>                                            </if>                                        </if>                                        <!--td><if condition="$vo.complete eq 1"><span style="color:red;">Yes</span><else/>No</if></td-->                                        <td><a href="{pigcms{$vo.url}" target="_blank">{pigcms{:L('I_ACCESS_URL')}</a></td>                                        <td>                                            <img src="{pigcms{$config.site_url}/upload/adver/{pigcms{$vo.pic}" style="width:300px;height:80px;" class="view_msg"/>                                        </td>                                        <td>                                            {pigcms{$vo.area_name}                                        </td>                                        <td class="textcenter">{pigcms{$vo.last_time|date='Y-m-d H:i:s',###}</td>                                        <td>                                            <if condition="$vo['status'] eq 1">                                                <span class="label label-primary">{pigcms{:L('B_STATACTIVE')}</span>                                                <elseif condition="$vo['status'] eq 0"/>                                                <span class="label label-warning">{pigcms{:L('B_STATCLD')}</span>                                            </if></td>                                        <td class="textcenter">                                            <!--a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Adver/adver_edit',array('id'=>$vo['id'],'frame_show'=>true))}','{pigcms{:L(\'BASE_VIEW\')}',600,420,true,false,false,false,'add',true);">{pigcms{:L('BASE_VIEW')}</a-->                                            <a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Adver/adver_edit',array('id'=>$vo['id']))}','{pigcms{:L(\'BASE_EDIT\')}',600,420,true,false,false,editbtn,'add',true);"><button class="btn btn-white text-grey" type="button">{pigcms{:L('BASE_EDIT')}</button></a>                                            <a href="javascript:void(0);" class="delete_row" parameter="id={pigcms{$vo.id}" url="{pigcms{:U('Adver/adver_del')}"><button class="btn btn-white text-grey" type="button">{pigcms{:L('BASE_DELETE')}</button></a>                                        </td>                                    </tr>                                </volist>                                <else/>                                <tr><td class="textcenter red" colspan="8">{pigcms{:L('_BACK_EMPTY_')}</td></tr>                            </if>                            </tbody>                            <tfoot>                            <tr>                            </tr>                            </tfoot>                        </table>                    </div>                </div>            </div>        </div><include file="Public:footer"/>