<include file="Public:header"/><div id="wrapper">    <include file="Public:left_menu"/>    <!----------------------------------------    以上不要写代码     ------------------------------------------------>    <div class="row wrapper border-bottom white-bg page-heading">        <div class="col-lg-9">            <h2>{pigcms{:L('I_ADCATEGORY_LIST')}</h2>            <ol class="breadcrumb">                <li class="breadcrumb-item">                    <a href="{pigcms{:U('Index/index')}">Home</a>                </li>                <!--                <li class="breadcrumb-item">-->                <!--                    <a>UI Elements</a>-->                <!--                </li>-->                <li class="breadcrumb-item active">                    <strong>{pigcms{:L('I_ADCATEGORY_LIST')}</strong>                </li>            </ol>        </div>        <div class="col-lg-3" style="height 90px;margin-top:40px;">            <a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Adver/cat_add')}','{pigcms{:L(\'I_ADD_CATEGORY\')}',680,380,true,false,false,addbtn,'add',true);"><button type="button" class="btn btn-primary btn-sm float-right">{pigcms{:L('I_ADD_CATEGORY')}</button></a>        </div>    </div>    <div class="wrapper wrapper-content animated fadeInRight">        <div class="row">            <div class="col-lg-12">                <div class="ibox">                    <div class="ibox-title">                        <h5>{pigcms{:L('G_EVENT_LIST')}</h5>                        <div class="ibox-tools">                        </div>                    </div>                    <div class="ibox-content">                        <div class="table-responsive">                            <!-------------------------------- 工具条 -------------------------------------->                            <div style="height: 0px;">                                <div id="tool_bar" class="form-inline tutti_toolbar">                                </div>                            </div>                        </div>                        <table class="table table-striped table-bordered table-hover dataTables-example">                            <thead>                            <tr>                                <th>{pigcms{:L('G_ID')}</th>                                <th>{pigcms{:L('G_NAME')}</th>                                <th>{pigcms{:L('I_SYMBOL')}</th>                                <th>{pigcms{:L('I_TYPE')}</th>                                <th>{pigcms{:L('I_AD_LIST')}</th>                                <th class="textcenter">{pigcms{:L('E_ACTION')}</th>                            </tr>                            </thead>                            <tbody>                            <if condition="is_array($category_list)">                                <volist name="category_list" id="vo">                                    <tr>                                        <td>{pigcms{$vo.cat_id}</td>                                        <td>{pigcms{$vo.cat_name}</td>                                        <td>{pigcms{$vo.cat_key}</td>                                        <td><if condition="$vo['cat_type']">PC<else />WAP</if></td>                                        <td><a href="{pigcms{:U('Adver/adver_list',array('cat_id'=>$vo['cat_id']))}">{pigcms{:L('I_AD_LIST')}</a></td>                                        <td class="textcenter">                                            <!--a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Adver/cat_edit',array('cat_id'=>$vo['cat_id'],'frame_show'=>true))}','{pigcms{:L(\'BASE_VIEW\')}',680,180,true,false,false,false,'add',true);"><button class="btn btn-white text-grey" type="button">{pigcms{:L('BASE_VIEW')}</button></a-->                                            <a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Adver/cat_edit',array('cat_id'=>$vo['cat_id']))}','{pigcms{:L(\'BASE_EDIT\')}',680,380,true,false,false,editbtn,'add',true);"><button class="btn btn-white text-grey" type="button">{pigcms{:L('BASE_EDIT')}</button></a>                                            <a href="javascript:void(0);" class="delete_row" parameter="cat_id={pigcms{$vo.cat_id}" url="{pigcms{:U('Adver/cat_del')}"><button class="btn btn-white text-grey" type="button">{pigcms{:L('BASE_DELETE')}</button></a>                                        </td>                                    </tr>                                </volist>                                <else/>                                <tr><td class="textcenter red" colspan="8">{pigcms{:L('_BACK_EMPTY_')}</td></tr>                            </if>                            </tbody>                            <tfoot>                            <tr>                            </tr>                            </tfoot>                        </table>                    </div>                </div>            </div>        </div><include file="Public:footer"/>