<include file="Public:header"/><div id="wrapper">    <include file="Public:left_menu"/>    <!----------------------------------------    以上不要写代码     ------------------------------------------------>    <div class="row wrapper border-bottom white-bg page-heading">        <div class="col-lg-6">            <h2>                <if condition="$_GET['type'] eq 2">{pigcms{:L('I_CITY_LIST')}                    <else/>                    {pigcms{:L('I_PROVINCE_LIST')}                </if>            </h2>            <ol class="breadcrumb">                <li class="breadcrumb-item">                    <a href="{pigcms{:U('Index/index')}">Home</a>                </li>                <!--                <li class="breadcrumb-item">-->                <!--                    <a>UI Elements</a>-->                <!--                </li>-->                <li class="breadcrumb-item active">                    <strong><a href="{pigcms{:U('Area/index')}">{pigcms{:L('I_PROVINCE_LIST')}</a></strong>                </li>                <li class="breadcrumb-item active">                    <if condition="($config['now_province'] && $_GET['type'] gt 2) || ($config['many_city'] && !$config['now_province'] && $_GET['type'] gt 1)">                        <strong><a href="{pigcms{:U('Area/index',array('pid'=>$now_area['area_pid'],'type'=>$_GET['type']-1))}"                            <if condition="$_GET['type'] eq 1">class="on"</if>                            >上一级</a></strong>                    </if>                    <if condition="!$config['many_city'] && $_GET['type'] gt 4">                        <strong><a href="{pigcms{:U('Area/index',array('pid'=>$now_area['area_pid'],'type'=>$_GET['type']-1))}"                            <if condition="$_GET['type'] eq 1">class="on"</if>                            >上一级</a></strong>                    </if>                    <if condition="$_GET['type'] eq 2">                        <strong>                            <a href="{pigcms{:U('Area/index',array('pid'=>$_GET['pid'],'type'=>$_GET['type']))}"                               class="on">                                {pigcms{:L('I_CITY_LIST')}                            </a>                        </strong>                    </if>                    <if condition="$_GET['type'] eq 3">                        <strong>                            <a href="{pigcms{:U('Area/index',array('pid'=>$_GET['pid'],'type'=>$_GET['type']))}"                               class="on">                                Region List                            </a>                        </strong>                    </if>                </li>            </ol>        </div>        <div class="col-lg-6" style="height 90px;margin-top:40px;">            <div class="btn-group float-right">                <a href="{pigcms{:U('Area/index')}"                <if condition="$_GET['type'] eq 1">class="on"</if>                >                <button type="button" class="btn btn-default btn-sm float-right text-grey">                    {pigcms{:L('I_PROVINCE_LIST')}                </button>                </a>                <a href="javascript:void(0);"                   onclick="window.top.artiframe('{pigcms{:U('Area/addadmin',array('area_id'=>$_GET['area_id']))}','{pigcms{:L(\'I_ADD_ADMIN\')}',450,320,true,false,false,addbtn,'add',true);">                    <button type="button" class="btn btn-default btn-sm float-right text-grey"                            style="margin-left:10px ">{pigcms{:L('I_ADD_ADMIN')}                    </button>                </a>            </div>        </div>    </div>    <div class="wrapper wrapper-content animated fadeInRight">        <div class="row">            <div class="col-lg-12">                <div class="ibox ">                    <div class="ibox-title tutti_hidden_obj">                        <h5>{pigcms{:L('I_ARTICLES')}</h5>                        <div class="ibox-tools">                        </div>                    </div>                    <div class="ibox-content">                        <!-------------------------------- 工具条 -------------------------------------->                        <div style="height: 0px;">                        </div>                        <table class="table table-striped table-bordered table-hover dataTables-example">                            <thead>                            <tr>                                <th>{pigcms{:L('I_ACCOUNT_NAME')}</th>                                <th>{pigcms{:L('B_FULLNAME')}</th>                                <th>{pigcms{:L('B_USERPHONE')}</th>                                <th>{pigcms{:L('B_USERPEMAIL')}</th>                                <!--th>QQ</th-->                                <th>{pigcms{:L('B_LASTLOGIN')}</th>                                <th>{pigcms{:L('B_NUMOFLOGIN')}</th>                                <th>{pigcms{:L('B_USERSTAT')}</th>                                <th>{pigcms{:L('B_ACTION')}</th>                            </tr>                            </thead>                            <tbody>                            <if condition="is_array($admin)">                                <volist name="admin" id="vo">                                    <tr>                                        <td>{pigcms{$vo.account}</td>                                        <td>{pigcms{$vo.realname}</td>                                        <td>{pigcms{$vo.phone}</td>                                        <td>{pigcms{$vo.email}</td>                                        <!--td>{pigcms{$vo.qq}</td-->                                        <td>{pigcms{$vo.last_time|date="Y-m-d H:i:s",###}</td>                                        <td>{pigcms{$vo.login_count}</td>                                        <td>                                            <if condition="$vo['status'] eq 1"><span style="color:green">{pigcms{:L('B_STATACTIVE')}</span>                                                <else/>                                                <span style="color:red">{pigcms{:L('B_STATCLD')}</span></if>                                        </td>                                        <td>                                            <a href="javascript:void(0);"                                               onclick="window.top.artiframe('{pigcms{:U('Area/addadmin',array('id'=>$vo['id'], 'area_id'=>$vo['area_id']))}','{pigcms{:L(\'BASE_EDIT\')}',450,320,true,false,false,editbtn,'add',true);">{pigcms{:L('BASE_EDIT')}</a>                                        </td>                                    </tr>                                </volist>                                <else/>                                <tr>                                    <td class="textcenter red" colspan="8">{pigcms{:L('_BACK_EMPTY_')}</td>                                </tr>                            </if>                            </tbody>                        </table>                    </div>                    </form>                </div>            </div>            <include file="Public:footer"/>