crumb-item active">                        <a href="{pigcms{:U('Area/index',array('pid'=>$parent_area["area_id"],'type'=>2))}" style="text-decoration: underline">【{pigcms{$parent_area["area_name"]}】 City List</a>                    </li>                    <li class="breadcrumb-item active">                    <strong>                        【{pigcms{$now_area["area_name"]}】  Region List                    </strong>                    </li>                </if>            </ol>        </div>        <div class="col-lg-3" style="height 90px;margin-top:40px;">            <a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Area/add',array('pid'=>$_GET['pid'],'type'=>$_GET['type']))}','Add',650,320,true,false,false,addbtn,'add',true);">                <button type="button" class="btn btn-primary btn-sm float-right">                <if condition="$_GET['type'] eq 2">                    Add a City                </if>                <if condition="$_GET['type'] eq 3">                    Add a Region                </if>                <if condition="$_GET['type'] eq 1">                    {pigcms{:L('I_ADD_A_PROVINCE')}                </if>                </button>            </a>        </div>    </div>    <div class="wrapper wrapper-content animated fadeInRight">        <div class="row">            <div class="col-lg-12">                <div class="ibox ">                    <div class="ibox-title tutti_hidden_obj">                        <h5>{pigcms{:L('I_ARTICLES')}</h5>                        <div class="ibox-tools">                        </div>                    </div>                    <div class="ibox-content">                        <div class="table-responsive">                            <!-------------------------------- 工具条 -------------------------------------->                            <div style="height: 0px;">                                <div id="tool_bar" class="form-inline tutti_toolbar">                                </div>                            </div>                        </div>                        <table class="table table-striped table-bordered table-hover dataTables-example">                            <thead>                            <tr>                                <th>{pigcms{:L('I_LISTING_ORDER')}</th>                                <th>{pigcms{:L('G_ID')}</th>                                <th>{pigcms{:L('G_NAME')}</th>                                <if condition="$_GET['type'] eq 2 || $_GET['type'] eq 4">                                    <th>{pigcms{:L('I_INITIAL_LETTER')}</th>                                </if>                                <th>{pigcms{:L('G_STATUS')}</th>                                <if condition="$_GET['type'] gt 1">                                    <th>{pigcms{:L('I_NETWOEK_SYMBOL')}</th>                                    <if condition="$_GET['type'] lt 4">                                        <th>{pigcms{:L('I_IP_SYMPOL')}</th>                                    </if>                                </if>                                <if condition="$_GET['type'] eq 2">                                    <th>View Regions</th>                                </if>                                <if condition="$_GET['type'] eq 3">                                    <th>View Malls</th>                                </if>                                <if condition="$_GET['type'] eq 1">                                    <th>{pigcms{:L('I_VIEW_CITIES')}</th>                                </if>                                <th>{pigcms{:L('E_ACTION')}</th>                            </tr>                            </tr>                            </thead>                            <tbody>                            <if condition="is_array($area_list)">                                <volist name="area_list" id="vo">                             