<include file="Public:header"/><div id="wrapper">    <include file="Public:left_menu"/>    <!----------------------------------------    以上不要写代码     ------------------------------------------------>    <div class="row wrapper border-bottom white-bg page-heading">        <div class="col-lg-9">            <h2>{pigcms{:L('DATA_EXPORT')}</h2>            <ol class="breadcrumb">                <li class="breadcrumb-item">                    <a href="{pigcms{:U('Index/index')}">Home</a>                </li>                <!--                <li class="breadcrumb-item">-->                <!--                    <a>UI Elements</a>-->                <!--                </li>-->                <li class="breadcrumb-item active">                    <strong><a href="{pigcms{:U('Area/index')}">{pigcms{:L('DATA_EXPORT')}</a></strong>                </li>            </ol>        </div>        <div class="col-lg-3" style="height 90px;margin-top:40px;">        </div>    </div>    <div class="wrapper wrapper-content animated fadeInRight">        <div class="row">            <div class="col-lg-12">                <div class="ibox ">                    <div class="ibox-title">                        <h5>{pigcms{:L('DATA_EXPORT')}</h5>                        <div class="ibox-tools">                        </div>                    </div>                    <div class="ibox-content">                        <p>                            1. Choose a form                        </p>                        <p id="export_menu">                            <volist name="export_menu" id="vo">                                <button type="button" <if condition="$i eq 1">class="btn btn-primary"<else/>class="btn btn-default"</if> data-type="{pigcms{$key}" style="margin: 5px 2px">{pigcms{$vo}</button>                            </volist>                        </p>                        <p>                            2. Sort Information                        </p>                        <p>                        <div class="col-lg-12">                            <div class="row">                                <div class="form-group col-lg-3">                                    <label>{pigcms{:L('F_FILTER')}</label>                                    <div class="input-group m-b">                                        <div class="input-group-prepend">                                            <button data-toggle="dropdown" class="btn btn-white dropdown-toggle" type="button" aria-expanded="false">Action </button>                                            <ul class="dropdown-menu" x-placement="bottom-start" style="position: absolute; top: 35px; left: 0px; will-change: top, left;">                                                <li><a href="javascript:void(0);">{pigcms{:L('_BACK_ORDER_NUM_')}</a></li>                                                <li><a href="javascript:void(0);">{pigcms{:L('_BACK_STORE_NAME_')}</a></li>                                                <li><a href="javascript:void(0);">{pigcms{:L('_BACK_USER_NAME_')}</a></li>                                                <li><a href="javascript:void(0);">{pigcms{:L('_BACK_USER_PHONE_')}</a></li>                                                <li><a href="javascript:void(0);">User ID</a></li>                                            </ul>                                        </div>                                        <input type="text" class="form-control" name="filter">                                    </div>                                </div>                                <div class="form-group col-lg-3" id="data_5">                                    <label class="font-normal">Date Range</label>                                    <div class="input-daterange input-group" id="datepicker">                                        <input type="text" class="form-control-sm form-control" name="start" value="{pigcms{:date('m/d/Y')}">                                        <!--span class="input-group-addon">to</span-->                                        <input type="text" class="form-control-sm form-control" name="end" value="{pigcms{:date('m/d/Y')}">                                    </div>                                </div>                                <div class="form-group col-lg-2">                                    <label>Order Status</label>                                    <div class="input-group m-b">                                        <div class="input-group-prepend">                                            <button data-toggle="dropdown" class="btn btn-white dropdown-toggle" type="button" aria-expanded="false">Action </button>                                            <ul class="dropdown-menu" x-placement="bottom-start" style="position: absolute; top: 35px; left: 0px; will-change: top, left;">                                                <volist name="status_list" id="vo">                                                <li><a href="javascript:void(0);">{pigcms{$vo}</a></li>                                                </volist>                                            </ul>                                        </div>                                    </div>                                </div>                                <div class="form-group col-lg-2">                                    <label>Payment Method</label>                                    <div class="input-group m-b">                                        <div class="input-group-prepend">                                            <button data-toggle="dropdown" class="btn btn-white dropdown-toggle" type="button" aria-expanded="false">Action </button>                                            <ul class="dropdown-menu" x-placement="bottom-start" style="position: absolute; top: 35px; left: 0px; will-change: top, left;">                                                <volist name="pay_method" id="vo">                                                    <li><a href="javascript:void(0);">{pigcms{$vo.name}</a></li>                                                </volist>                                            </ul>                                        </div>                                    </div>                                </div>                                <div class="form-group col-lg-2">                                    <label>City</label>                                    <div class="input-group m-b">                                        <div class="input-group-prepend">                                            <button data-toggle="dropdown" class="btn btn-white dropdown-toggle" type="button" aria-expanded="false">Action </button>                                            <ul class="dropdown-menu" x-placement="bottom-start" style="position: absolute; top: 35px; left: 0px; will-change: top, left;">                                                <volist name="city" id="vo">                                                    <li><a href="javascript:void(0);">{pigcms{$vo.area_name}</a></li>                                                </volist>                                            </ul>                                        </div>                                    </div>                                </div>                            </div>                            <button type="button" class="btn btn-primary btn-lg">Download</button>                        </div>                        </p>                    </div>                </div>            </div>        </div>        <link href="{pigcms{$static_path}css/plugins/datapicker/datepicker3.css" rel="stylesheet">        <script src="{pigcms{$static_path}js/plugins/datapicker/bootstrap-datepicker.js"></script>        <script>            $('#export_menu').children('button').each(function () {                $(this).click(function () {                    $(this).addClass("btn-primary").siblings().removeClass('btn-primary');                    $(this).removeClass('btn-default').siblings().addClass("btn-default");                    changeFilter();                });            });                        $('.dropdown-menu').find("li a").each(function () {                $(this).click(function () {                    var name = $(this).html();                    $(this).parents('.dropdown-menu').siblings('.dropdown-toggle').html(name);                });            });            function changeFilter() {                var type = $('#export_menu').children('button.btn-primary').data('type');            }            $('#data_5 .input-daterange').datepicker({                keyboardNavigation: false,                forceParse: false,                autoclose: true            });        </script><include file="Public:footer"/>