<include file="Public:header"/><div id="wrapper">    <include file="Public:left_menu"/>    <!----------------------------------------    以上不要写代码     ------------------------------------------------>    <div class="row wrapper border-bottom white-bg page-heading">        <div class="col-lg-9">            <h2>Record</h2>            <ol class="breadcrumb">                <li class="breadcrumb-item">                    {pigcms{:L('_BACK_DLVMNG_')}                </li>                <!--                <li class="breadcrumb-item">-->                <!--                    <a>UI Elements</a>-->                <!--                </li>-->                <li class="breadcrumb-item active">                    <strong>Record</strong>                </li>            </ol>        </div>        <div class="col-lg-3" style="height 90px;margin-top:40px;">        </div>    </div>    <div class="wrapper wrapper-content animated fadeInRight">        <div class="row">            <div class="col-lg-12">                <div class="ibox ">                    <div class="ibox-title tutti_hidden_obj">                    </div>                    <div class="ibox-content">                        <div class="table-responsive">                            <!-------------------------------- 工具条 -------------------------------------->                            <div style="height: 0px;">                            </div>                            <table class="table table-striped table-bordered table-hover dataTables-example">                                <thead>                                <tr>                                    <th  data-toggle="true">{pigcms{:L('B_ACCOUNTID')}</th>                                    <th>Order ID</th>                                    <th>Curier ID</th>                                    <th>Time</th>                                    <th>Hand Order Number</th>                                    <th>Pick</th>                                    <th>A</th>                                    <th>B</th>                                    <th>C</th>                                    <th>D</th>                                    <th>Logic</th>                                </tr>                                </thead>                                <tbody>                                    <if condition="is_array($list)">                                        <volist name="list" id="vo">                                            <tr class="gradeX">                                                <td>{pigcms{$vo.id}</td>                                                <td>{pigcms{$vo.order_id}</td>                                                <td>{pigcms{$vo.deliver_id}</td>                                                <td>{pigcms{$vo.send_time|date='Y-m-d H:i:s',###}</td>                                                <td>{pigcms{$vo.hand_order}</td>                                                <td>{pigcms{$vo.order_is_pick}</td>                                                <td>{pigcms{$vo.is_a}</td>                                                <td>{pigcms{$vo.is_b}</td>                                                <td>{pigcms{$vo.is_c}</td>                                                <td>{pigcms{$vo.is_d}</td>                                                <td class="textcenter">{pigcms{$vo.logic_num}</td>                                            </tr>                                        </volist>                                    <else/>                                        <tr>                                            <td class="textcenter red" colspan="11">{pigcms{:L('_BACK_EMPTY_')}</td>                                        </tr>                                    </if>                                </tbody>                                <tfoot>                                <tr>                                </tr>                                </tfoot>                            </table>                        </div>                    </div>                </div>            </div>        </div><script type="text/javascript" src="./static/js/artdialog/jquery.artDialog.js"></script><script type="text/javascript" src="./static/js/artdialog/iframeTools.js"></script><script type="text/javascript">var test;$(document).ready(function(){	$('.see_qrcode').on('click', function(){		test = art.dialog.open($(this).attr('href'),{			init: function(){				var iframe = this.iframe.contentWindow;				window.top.art.dialog.data('iframe_handle',iframe);			},			id: 'handle',			title:'扫描二维码绑定微信号',			padding: 0,			width: 430,			height: 433,			lock: true,			resize: false,			background:'black',			button: null,			fixed: false,			close: function(){clearInterval(t);},			left: '50%',			top: '38.2%',			opacity:'0.4'		});		var id = $(this).attr('data-id'), obj = $(this);	 	var t = window.setInterval(function(){			$.get("{pigcms{:U('Index/check_account')}", {id:id},  function(result){				if (result.error_code == 0) {					test.close();					clearInterval(t);					obj.parent('td').html(result.nickname).siblings('td').children('.cancel').show();				}			}, 'json');		},3000);		return false;	});	$('.cancel').click(function(){		var id = $(this).attr('data-id'), obj = $(this);		obj.attr('disabled', true);		$.get("{pigcms{:U('Index/cancel_account')}", {id:id}, function(result){			obj.attr('disabled', false);			if (result.error_code == 1) {				alert(result.msg);			} else {				var qrcode_id = 3890000000 + id;				obj.hide().parent('td').siblings('.nickname').html('<a href="{pigcms{$config.site_url}/index.php?g=Index&c=Recognition&a=see_tmp_qrcode&qrcode_id=' + qrcode_id + '&img=1" data-id="' + id + '" class="see_qrcode" style="color:green">绑定微信号</a>');			}		}, 'json');	});});</script><include file="Public:footer"/>