<include file="Public:header"/><div id="wrapper">    <include file="Public:left_menu"/>    <!----------------------------------------    以上不要写代码     ------------------------------------------------>    <div class="row wrapper border-bottom white-bg page-heading">        <div class="col-lg-9">            <h2>{pigcms{:L('G_EVENT_LIST')}</h2>            <ol class="breadcrumb">                <li class="breadcrumb-item">                    <a href="{pigcms{:U('Index/index')}">Home</a>                </li>                <!--                <li class="breadcrumb-item">-->                <!--                    <a>UI Elements</a>-->                <!--                </li>-->                <li class="breadcrumb-item active">                    <strong>{pigcms{:L('G_EVENT_LIST')}</strong>                </li>            </ol>        </div>        <div class="col-lg-3" style="height 90px;margin-top:40px;">            <a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Event/add_coupon',array('event_id'=>$event['id']))}','{pigcms{:L(\'G_ADD_COUPON\')}',680,300,true,false,false,addbtn,'add',true);"><button type="button" class="btn btn-primary btn-sm float-right">{pigcms{:L('G_ADD_COUPON')}</button></a>        </div>    </div>    <div class="wrapper wrapper-content animated fadeInRight">        <div class="row">            <div class="col-lg-12">                <div class="ibox ">                    <div class="ibox-title">                        <h5>{pigcms{:L('G_EVENT_LIST')}</h5>                        <div class="ibox-tools">                        </div>                    </div>                    <div class="ibox-content">                        <div class="table-responsive">                            <!-------------------------------- 工具条 -------------------------------------->                            <div style="height: 50px;">                                <div id="tool_bar" class="form-inline tutti_toolbar">                                </div>                            </div>                        </div>                        <table class="table table-striped table-bordered table-hover dataTables-example">                            <thead>                            <tr>                                <th>{pigcms{:L('G_ID')}</th>                                <th>{pigcms{:L('G_NAME')}</th>                                <th>{pigcms{:L('G_MIN_ORDER')}</th>                                <th>                                    <if condition="$event['type'] neq 6">                                        {pigcms{:L('G_DISCOUNT_AMOUNT')}                                        <else />                                        {pigcms{:L('G_DISCOUNT_AMOUNT_GOODS')}                                    </if>                                </th>                                <th>                                    {pigcms{$event['type_name']}                                </th>                                <th>{pigcms{:L('G_NUMBER_CLAIMS')}</th>                                <th>{pigcms{:L('G_NUMBER_USED')}</th>                                <th>{pigcms{:L('G_EXPIRED_AMOUNT')}</th>                                <th>                                    <if condition="$event['type'] eq 3 or $event['type'] eq 4 or $event['type'] eq 5">                                        {pigcms{:L('G_COMBINED')}                                        <elseif condition="$event['type'] eq 6" />                                        Apply discount to options                                        <else />                                        {pigcms{:L('G_COUP_TYPE')}                                    </if>                                </th>                                <if condition="$system_session['level'] eq 2 || $system_session['level'] eq 0">                                    <th class="textcenter">{pigcms{:L('G_ACTION')}</th>                                </if>                            </tr>                            </thead>                            <tbody>                            <if condition="is_array($coupon_list)">                                <volist name="coupon_list" id="vo">                                    <tr>                                        <td>{pigcms{$vo.id}</td>                                        <td>{pigcms{$vo.name}</td>                                        <td>{pigcms{$vo.use_price}</td>                                        <td>{pigcms{$vo.discount}</td>                                        <if condition="$vo['store_name']">                                            <td>{pigcms{$vo.store_name}</td>                                            <else />                                            <td>{pigcms{$vo.limit_day}</td>                                        </if>                                        <td>{pigcms{$vo.all_num}</td>                                        <td>{pigcms{$vo.use_num}</td>                                        <td>{pigcms{$vo.expiry_num}</td>                                        <td>                                            <if condition="$vo['type'] eq 0">                                                <if condition="$event['type'] eq 3 or $event['type'] eq 4 or $event['type'] eq 5">                                                    {pigcms{:L('G_NO')}                                                    <elseif condition="$event['type'] eq 6" />                                                    NO                                                    <else />                                                    {pigcms{:L('G_INVITEE')}                                                </if>                                                <else />                                                <if condition="$event['type'] eq 3 or $event['type'] eq 4 or $event['type'] eq 5">                                                    {pigcms{:L('G_YES')}                                                    <elseif condition="$event['type'] eq 6" />                                                    YES                                                    <else />                                                    {pigcms{:L('G_INVITER')}                                                </if>                                            </if>                                        </td>                                        <if condition="$system_session['level'] eq 2 || $system_session['level'] eq 0">                                            <td class="textcenter">                                                <a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Event/add_coupon',array('id'=>$vo['id']))}','{pigcms{:L(\'G_EDIT\')}',680,370,true,false,false,editbtn,'add',true);"><button class="btn btn-white text-grey" type="button">{pigcms{:L('G_EDIT')}</button></a>                                                <if condition="$event['type'] eq 4 or $event['type'] eq 5">                                                    <a href="javascript:void(0);" class="delete_row" parameter="id={pigcms{$vo.id}" url="{pigcms{:U('Event/coupon_del')}"><button class="btn btn-white text-grey" type="button">{pigcms{:L('QW_DELETE')}</button></a>                                                </if>                                            </td>                            </if>                            </tr>                            </volist>                            <else/>                            <tr><td class="textcenter red" colspan="10">{pigcms{:L('_BACK_EMPTY_')}</td></tr>                            </if>                            </tbody>                            <tfoot>                            <tr>                            </tr>                            </tfoot>                        </table>                    </div>                </div>            </div>        </div><include file="Public:footer"/>