<include file="Public:header"/><div id="wrapper">    <include file="Public:left_menu"/>    <!----------------------------------------    以上不要写代码     ------------------------------------------------>    <div class="row wrapper border-bottom white-bg page-heading">        <div class="col-lg-9">            <h2>{pigcms{:L('EVENT_NOTIFICATIONS')}</h2>            <ol class="breadcrumb">                <li class="breadcrumb-item">                    {pigcms{:L('_BACK_MARKETING_')}                </li>                <!--                <li class="breadcrumb-item">-->                <!--                    <a>UI Elements</a>-->                <!--                </li>-->                <li class="breadcrumb-item active">                    <strong>{pigcms{:L('EVENT_NOTIFICATIONS')}</strong>                </li>            </ol>        </div>        <div class="col-lg-3" style="height 90px;margin-top:40px;">            <a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Event/add_message')}','{pigcms{:L(\'BASE_ADD\')}',680,400,true,false,false,addbtn,'add',true);"><button type="button" class="btn btn-primary btn-sm float-right">{pigcms{:L('BASE_ADD')}</button></a>        </div>    </div>    <div class="wrapper wrapper-content animated fadeInRight">        <div class="row">            <div class="col-lg-12">                <div class="ibox ">                    <div class="ibox-title tutti_hidden_obj">                        <h5>{pigcms{:L('G_EVENT_LIST')}</h5>                        <div class="ibox-tools">                        </div>                    </div>                    <div class="ibox-content">                        <div class="table-responsive">                            <!-------------------------------- 工具条 -------------------------------------->                            <div style="margin-bottom: 10px;min-height: 50px">                                <form action="{pigcms{:U('Event/message')}" method="get">                                    <input type="hidden" name="c" value="Event"/>                                    <input type="hidden" name="a" value="message"/>                                    <div id="tool_bar" class="form-inline">                                        {pigcms{:L('G_TYPE')}:                                        <select name="type_select" id="type_select" class="form-control">                                            <option value="-1" <if condition="$_GET['type_select'] eq -1 or $_GET['type_select'] eq ''">selected="selected"</if>>All</option>                                            <volist name="type_list" id="vo">                                                <option value="{pigcms{$key}"                                                <if condition="$_GET['type_select'] eq $key and $_GET['type_select'] neq ''">selected="selected"</if>>{pigcms{$vo}</option>                                            </volist>                                        </select>                                        &nbsp;                                        <input type="submit" value="{pigcms{:L('_BACK_SEARCH_')}" class="form-control"/>                                    </div>                                </form>                            </div>                        <table class="table table-striped table-bordered table-hover dataTables-example">                            <thead>                            <tr>                                <th>{pigcms{:L('G_NAME')}</th>                                <th>{pigcms{:L('G_TYPE')}</th>                                <th>Equals</th>                                <th>{pigcms{:L('I_TITLE')}</th>                                <th>{pigcms{:L('G_STATUS')}</th>                                <th>Sorting #</th>                                <th>Time</th>                                <if condition="$system_session['level'] eq 2 || $system_session['level'] eq 0">                                    <th class="textcenter">{pigcms{:L('G_ACTION')}</th>                                </if>                            </tr>                            </thead>                            <tbody>                            <if condition="is_array($list)">                                <volist name="list" id="vo">                                    <tr>                                        <td>{pigcms{$vo.name}</td>                                        <td>{pigcms{$vo.type_name}</td>                                        <td>                                            {pigcms{$vo.days}                                        </td>                                        <td>                                            {pigcms{$vo.title}                                        </td>                                        <td>                                            <if condition="$vo['status'] eq 1">                                                <span class="label label-primary">{pigcms{:L('_BACK_NORMAL_')}</span>                                                <else />                                                <span class="label label-warning">{pigcms{:L('_BACK_FORBID_')}</span>                                            </if>                                        </td>                                        <td>                                            {pigcms{$vo.sort}                                        </td>                                        <td>                                            {pigcms{$vo.send_time}                                        </td>                                        <if condition="$system_session['level'] eq 2 || $system_session['level'] eq 0">                                            <td class="textcenter">                                                <a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Event/add_message',array('type'=>$vo['type'],'days'=>$vo['days']))}','{pigcms{:L(\'G_EDIT\')}',680,370,true,false,false,editbtn,'add',true);"><button class="btn btn-white text-grey" type="button">{pigcms{:L('G_EDIT')}</button></a>                                                <a href="javascript:void(0);" class="delete_row" parameter="type={pigcms{$vo.type}&days={pigcms{$vo.days}" url="{pigcms{:U('Event/del_message')}"><button class="btn btn-white text-grey" type="button">{pigcms{:L('QW_DELETE')}</button></a>                                            </td>                                        </if>                                    </tr>                                </volist>                                <td class="textcenter pagebar" colspan="7">{pigcms{$pagebar}</td>                                <else/>                                <tr><td class="textcenter red" colspan="7">{pigcms{:L('_BACK_EMPTY_')}</td></tr>                            </if>                            </tbody>                            <tfoot>                            <tr>                            </tr>                            </tfoot>                        </table>                        </div>                    </div>                </div>            </div>        </div><include file="Public:footer"/>