<include file="Public:header"/><div id="wrapper">    <include file="Public:left_menu"/>    <!----------------------------------------    以上不要写代码     ------------------------------------------------>    <div class="row wrapper border-bottom white-bg page-heading">        <div class="col-lg-10">            <h2>{pigcms{:L('I_BOTTOM_NAVIGATION')}</h2>            <ol class="breadcrumb">                <li class="breadcrumb-item">                    <a href="{pigcms{:U('Index/index')}">Home</a>                </li>                <!--                <li class="breadcrumb-item">-->                <!--                    <a>UI Elements</a>-->                <!--                </li>-->                <li class="breadcrumb-item active">                    <strong>{pigcms{:L('I_BOTTOM_NAVIGATION')}</strong>                </li>            </ol>        </div>        <div class="col-lg-2">        </div>    </div>    <div class="row wrapper wrapper-content animated fadeInRight">        <div class="col-lg-12">            <div class="ibox ">                <div class="ibox-content">                    <div id="nav" class="mainnav_title">                        <ul>                            <a href="{pigcms{:U('Footer/index')}" class="on">{pigcms{:L('I_BOTTOM_NAVIGATION')}</a>|                            <a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Footer/add')}','{pigcms{:L(\'I_ADD_NAVIGATION\')}',800,460,true,false,false,addbtn,'add',true);">{pigcms{:L('I_ADD_NAVIGATION')}</a>                        </ul>                    </div>                    <div class="table-list">                        <table width="100%" cellspacing="0">                            <colgroup>                                <col/>                                <col/>                                <col/>                                <col width="180" align="center"/>                            </colgroup>                            <thead>                                <tr>                                    <th>{pigcms{:L('G_ID')}</th>                                    <th>{pigcms{:L('G_NAME')}</th>                                    <th>{pigcms{:L('I_TITLE')}</th>                                    <th class="textcenter">{pigcms{:L('E_ACTION')}</th>                                </tr>                            </thead>                            <tbody>                                <if condition="is_array($link_list)">                                    <volist name="link_list" id="vo">                                        <tr>                                            <td>{pigcms{$vo.id}</td>                                            <td>{pigcms{$vo.name}</td>                                            <td>{pigcms{$vo.title}</td>                                            <td class="textcenter"><a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Footer/edit',array('id'=>$vo['id']))}','{pigcms{:L(\'BASE_EDIT\')}',800,460,true,false,false,editbtn,'edit',true);">{pigcms{:L('BASE_EDIT')}</a> | <a href="javascript:void(0);" class="delete_row" parameter="id={pigcms{$vo.id}" url="{pigcms{:U('Footer/del')}">{pigcms{:L('BASE_DELETE')}</a></td>                                        </tr>                                    </volist>                                    <tr><td class="textcenter pagebar" colspan="4">{pigcms{$pagebar}</td></tr>                                <else/>                                    <tr><td class="textcenter red" colspan="4">{pigcms{:L('_BACK_EMPTY_')}</td></tr>                                </if>                            </tbody>                        </table>                    </div>                </div>            </div>        </div><include file="Public:footer"/>