<include file="Public:header"/><div id="wrapper">    <include file="Public:left_menu"/>    <!----------------------------------------    以上不要写代码     ------------------------------------------------>    <div class="row wrapper border-bottom white-bg page-heading">        <div class="col-lg-9">            <h2>{pigcms{:L('_BACK_MER_LIST_')}</h2>            <ol class="breadcrumb">                <li class="breadcrumb-item">                    <a href="{pigcms{:U('Index/index')}">Home</a>                </li>                <!--                <li class="breadcrumb-item">-->                <!--                    <a>UI Elements</a>-->                <!--                </li>-->                <li class="breadcrumb-item active">                    <strong>{pigcms{:L('_BACK_MER_LIST_')}</strong>                </li>            </ol>        </div>        <div class="col-lg-3" style="height 90px;margin-top:40px">            <b>{pigcms{:L('_BACK_TOTAL_MER_BA_')}：{pigcms{$all_money}</b>        </div>    </div>    <!--form data-->    <!--  form data end --->    <div class="wrapper wrapper-content animated fadeInRight">        <div class="row">            <div class="col-lg-12">                <div class="ibox ">                    <div class="ibox-title">                        <h5>{pigcms{:L('_BACK_MER_LIST_')}</h5>                        <div class="ibox-tools">                            <if condition="$system_session['level'] neq 3">                                        <span style="margin-left:40px">                                            <b>{pigcms{:L('_BACK_TOTAL_MER_BA_')}：{pigcms{$all_money}</b>                                        </span>                            </if>                            <!--                            <a class="collapse-link">-->                            <!--                                <i class="fa fa-chevron-up"></i>-->                            <!--                            </a>-->                            <!--                            <a class="dropdown-toggle" data-toggle="dropdown" href="#">-->                            <!--                                <i class="fa fa-wrench"></i>-->                            <!--                            </a>-->                            <!--                            <ul class="dropdown-menu dropdown-user">-->                            <!--                                <li><a href="#" class="dropdown-item">Config option 1</a>-->                            <!--                                </li>-->                            <!--                                <li><a href="#" class="dropdown-item">Config option 2</a>-->                            <!--                                </li>-->                            <!--                            </ul>-->                            <!--                            <a class="close-link">-->                            <!--                                <i class="fa fa-times"></i>-->                            <!--                            </a>-->                        </div>                    </div>                    <div class="ibox-content">                        <div class="table-responsive">                            <!-------------------------------- 工具条 -------------------------------------->                            <div style="height: 50px;">                                <form action="{pigcms{:U('Merchant/index')}" class="form-inline" role="form"                                      method="get">                                    <div id="tool_bar" style="form-group tutti_toolbar" style="height: 80px;">                                        <if condition="$system_session['level'] neq 3">                                            City:                                            <select name="searchtype" id="city_select" class="form-control">                                                <option value="0"                                                <if condition="$city_id eq '' or $city_id eq 0">selected="selected"</if>                                                >All</option>                                                <volist name="city" id="vo">                                                    <option value="{pigcms{$vo.area_id}"                                                    <if condition="$city_id eq $vo['area_id']">selected="selected"</if>                                                    >{pigcms{$vo.area_name}</option>                                                </volist>                                            </select>                                        </if>                                        <input type="hidden" name="c" value="Merchant"/>                                        <input type="hidden" name="a" value="index"/>                                        &nbsp;{pigcms{:L('_BACK_SEARCH_')}: <input type="text" name="keyword"                                                                                   value="{pigcms{$_GET['keyword']}"                                                                                   class="form-control"/>                                        <select name="searchtype" class="form-control">                                            <option value="name"                                            <if condition="$_GET['searchtype'] eq 'name'">selected="selected"</if>                                            >{pigcms{:L('_BACK_MER_NAME_')}</option>                                            <option value="account"                                            <if condition="$_GET['searchtype'] eq 'account'">selected="selected"</if>                                            >{pigcms{:L('_BACK_MER_ACC_')}</option>                                            <option value="phone"                                            <if condition="$_GET['searchtype'] eq 'phone'">selected="selected"</if>                                            >{pigcms{:L('_BACK_MER_PHONE_')}</option>                                            <option value="mer_id"                                            <if condition="$_GET['searchtype'] eq 'mer_id'">selected="selected"</if>                                            >{pigcms{:L('_BACK_MER_ID_')}</option>                                        </select>                                        &nbsp;{pigcms{:L('_BACK_MER_STATUS_')}: <select name="searchstatus"                                                                                        class="form-control">                                            <option value="0"                                            <if condition="$_GET['searchstatus'] eq 0">selected="selected"</if>                                            >{pigcms{:L('_BACK_NORMAL_')}</option>                                            <option value="1"                                            <if condition="$_GET['searchstatus'] eq '1'">selected="selected"</if>                                            >{pigcms{:L('_BACK_PENDING_')}</option>                                            <option value="2"                                            <if condition="$_GET['searchstatus'] eq '2'">selected="selected"</if>                                            >{pigcms{:L('_BACK_CLOSED_')}</option>                                            <option value="3"                                            <if condition="$_GET['searchstatus'] eq '3'">selected="selected"</if>                                            >{pigcms{:L('_BACK_ALL_')}</option>                                        </select>                                        &nbsp;{pigcms{:L('_BACK_SORT_ORDER_')}: <select name="searchorder"                                                                                        class="form-control">                                            <option value="0"                                            <if condition="$_GET['searchorder'] eq 0">selected="selected"</if>                                            >{pigcms{:L('_BACK_CODE_')}</option>                                            <option value="1"                                            <if condition="$_GET['searchorder'] eq '1'">selected="selected"</if>                                            >{pigcms{:L('_BACK_MER_BALANCE_')}</option>                                        </select>                                        &nbsp;<input type="submit" value="{pigcms{:L('_BACK_SEARCH_')}"                                                     class="form-control"/>                                    </div>                                </form>                            </div>                            <!------------------------------------------------------------------------------>                            <!-- <form name="myform" id="myform" action="" method="post">-->                            <table class="footable table table-stripped toggle-arrow-tiny">                                <thead>                                <tr>                                    <th  data-toggle="true">{pigcms{:L('_BACK_CODE_')}</th>                                    <th>{pigcms{:L('_BACK_MER_NAME_')}</th>                                    <th>{pigcms{:L('_BACK_MER_PHONE_')}</th>                                    <th>{pigcms{:L('_BACK_LAST_TIME_')}</th>                                    <th>{pigcms{:L('_BACK_VISIT_')}</th>                                    <th>{pigcms{:L('_BACK_ACC_BALANCE_')}</th>                                    <th>{pigcms{:L('_BACK_STATUS_')}</th>                                    <if condition="C('config.open_extra_price') eq 1">                                        <th>{pigcms{:C('config.extra_price_alias_name')}</th>                                    </if>                                    <if condition="$system_session['level'] neq 3">                                        <th>{pigcms{:L('_BACK_MER_INVOICE_')}</th>                                    </if>                                    <th>{pigcms{:L('_BACK_CZ_')}</th>                                </tr>                                </thead>                                <tbody>                            </table>                            <table class="table table-striped table-bordered table-hover dataTables-example">                                <thead>                                <tr>                                    <th>{pigcms{:L('_BACK_CODE_')}</th>                                    <th>{pigcms{:L('_BACK_MER_NAME_')}</th>                                    <th>{pigcms{:L('_BACK_MER_PHONE_')}</th>                                    <th>{pigcms{:L('_BACK_LAST_TIME_')}</th>                                    <th>{pigcms{:L('_BACK_VISIT_')}</th>                                    <th>{pigcms{:L('_BACK_ACC_BALANCE_')}</th>                                    <th>{pigcms{:L('_BACK_STATUS_')}</th>                                    <if condition="C('config.open_extra_price') eq 1">                                        <th>{pigcms{:C('config.extra_price_alias_name')}</th>                                    </if>                                    <if condition="$system_session['level'] neq 3">                                        <th>{pigcms{:L('_BACK_MER_INVOICE_')}</th>                                    </if>                                    <th>{pigcms{:L('_BACK_CZ_')}</th>                                </tr>                                </thead>                                <tbody>                                <if condition="is_array($merchant_list)">                                    <volist name="merchant_list" id="vo">                                        <tr class="gradeX">                                            <td>{pigcms{$vo.mer_id}</td>                                            <td>{pigcms{$vo.name}</td>                                            <td>{pigcms{$vo.phone}</td>                                            <td>                                                <if condition="$vo['last_time']">{pigcms{$vo.last_time|date='Y-m-d                                                    H:i:s',###}                                                    <else/>                                                    N/A                                                </if>                                            </td>                                            <td class="textcenter">                                                <if condition="$vo['status'] eq 1 OR $vo['status'] eq 3"><a                                                            href="{pigcms{:U('Merchant/merchant_login',array('mer_id'=>$vo['mer_id']))}"                                                            class="__full_screen_link" target="_blank">{pigcms{:L('_BACK_VISIT_')}</a>                                                    <else/>                                                    <a href="javascript:alert('{pigcms{:L(\'K_SUSPENDED\')}');"                                                       class="__full_screen_link">访问</a></if>                                            </td>                                            <!--td class="textcenter">{pigcms{$vo.hits}</td>                                            <td class="textcenter">{pigcms{$vo.fans_count}</td-->                                            <td class="textcenter">{pigcms{$vo.money}</td>                                            <td>                                                <if condition="$vo['status'] eq 1"><font color="green">{pigcms{:L('_BACK_ACTIVE_')}</font>                                                    <elseif condition="$vo['status'] eq 2"/>                                                    <font color="red">{pigcms{:L('_BACK_PENDING_')}</font>                                                    <elseif condition="$vo['status'] eq 3"/>                                                    <font color="red">欠款</font>                                                    <else/>                                                    <font color="red">关闭</font></if>                                            </td>                                            <if condition="C('config.open_extra_price') eq 1">                                                <td>                                                    商家欠平台{pigcms{$vo.extra_price_pay_for_system}个{pigcms{:C('config.extra_price_alias_name')},即{pigcms{:sprintf("%.2f",$vo['extra_price_pay_for_system']*$vo['extra_price_percent']/100)}元                                                </td>                                            </if>                                            <if condition="$system_session['level'] neq 3">                                                <td class="textcenter"><a                                                            href="{pigcms{:U('Bill/merchant_money_list',array('mer_id'=>$vo['mer_id']))}">{pigcms{:L('_BACK_INVOICE_')}</a>                                                </td>                                            </if>                                            <!--td class="textcenter"><a href="{pigcms{:U('Merchant/weidian_order',array('mer_id'=>$vo['mer_id']))}">微店账单</a></td-->                                            <td class="textcenter">                                                <a href="{pigcms{:U('Merchant/store',array('mer_id'=>$vo['mer_id']))}">{pigcms{:L('_BACK_STORE_LIST_')}</a>                                                |                                                <!--<a href="javascript:void(0);" onclick="window.top.show_other_frame('Group','product','mer_id={pigcms{$vo.mer_id}')">{pigcms{$config.group_alias_name}列表</a> | -->                                                <!--<a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Merchant/edit',array('mer_id'=>$vo['mer_id'],'frame_show'=>true))}','查看详细信息',520,370,true,false,false,false,'detail',true);">查看</a> | -->                                                <a href="javascript:void(0);"                                                   onclick="window.artiframe('{pigcms{:U('Merchant/edit',array('mer_id'=>$vo['mer_id']))}','{pigcms{:L(\'_BACK_EDIT_MER_INFO_\')}',800,560,true,false,false,editbtn,'edit',true);">{pigcms{:L('_BACK_EDIT_')}</a>                                                |                                                <a href="javascript:void(0);" class="delete_row"                                                   parameter="mer_id={pigcms{$vo.mer_id}"                                                   url="{pigcms{:U('Merchant/del')}">{pigcms{:L('_BACK_DEL_')}</a>                                            </td>                                        </tr>                                    </volist>                                    <else/>                                    <tr>                                        <td class="textcenter red"                                        <if condition="$system_session['level'] neq 3">colspan="9"                                            <else/>                                            colspan="8"                                        </if>                                        >{pigcms{:L('_BACK_EMPTY_')}</td></tr>                                </if>                                </tbody>                                <tfoot>                                <tr>                                </tr>                                </tfoot>                            </table>                            {pigcms{$pagebar}                            <!--                            </form>-->                        </div>                    </div>                </div>            </div>        </div>    </div>    <!-- Page-Level Scripts -->    <script>        $(document).ready(function () {            if (0) {                $('.dataTables-example').DataTable({                    pageLength: 25,                    responsive: true,                    dom: '<"html5buttons"B>lTfgitp',                    buttons: [                        {extend: 'copy'},                        {extend: 'csv'},                        {extend: 'excel', title: 'ExampleFile'},                        {extend: 'pdf', title: 'ExampleFile'},                        {                            extend: 'print',                            customize: function (win) {                                $(win.document.body).addClass('white-bg');                                $(win.document.body).css('font-size', '10px');                                $(win.document.body).find('table')                                    .addClass('compact')                                    .css('font-size', 'inherit');                            }                        }                    ]                });            }        });    </script>    <!----------------------------------------    以下不要写代码     ------------------------------------------------>    <include file="Public:footer"/>