<include file="Public:header"/><div id="wrapper">    <include file="Public:left_menu"/>    <!----------------------------------------    以上不要写代码     ------------------------------------------------>    <div class="row wrapper border-bottom white-bg page-heading">        <div class="col-lg-9">            <h2>Integration Log</h2>            <ol class="breadcrumb">                <li class="breadcrumb-item">                    {pigcms{:L('_BACK_MERCHANTMNG_')}                </li>                <!--                <li class="breadcrumb-item">-->                <!--                    <a>UI Elements</a>-->                <!--                </li>-->                <li class="breadcrumb-item active">                    <strong>Integration Log</strong>                </li>            </ol>        </div>        <div class="col-lg-3" style="height 90px;margin-top:40px;">        </div>    </div>    <div class="wrapper wrapper-content animated fadeInRight">        <div class="row">            <div class="col-lg-12">                <div class="ibox ">                    <div class="ibox-title tutti_hidden_obj">                        <h5>Integration Log</h5>                        <div class="ibox-tools">                        </div>                    </div>                    <div class="ibox-content">                        <div class="table-responsive">                            <!-------------------------------- 工具条 -------------------------------------->                            <div style="margin-bottom: 10px;min-height: 50px">                                <form action="{pigcms{:U('Merchant/integration')}" class="form-inline" role="form" method="get">                                    <div id="tool_bar" class="form-group">                                        <input type="hidden" name="c" value="Merchant"/>                                        <input type="hidden" name="a" value="integration"/>                                        &nbsp;{pigcms{:L('_BACK_SEARCH_')}:                                        <input type="text" name="keyword" value="{pigcms{$_GET['keyword']}" class="form-control" placeholder="Store ID"/>                                        &nbsp;Type:                                        <select name="searchstatus" class="form-control">{pigcms{$_GET['searchstatus']}                                            <option value="0" <if condition="$_GET['searchstatus'] eq 0">selected="selected"</if>>                                            All                                            </option>                                            <option value="1" <if condition="$_GET['searchstatus'] eq '1'">selected="selected"</if>>                                            Order                                            </option>                                            <option value="2" <if condition="$_GET['searchstatus'] eq '2'">selected="selected"</if>>                                            Busy Mode                                            </option>                                            <option value="3" <if condition="$_GET['searchstatus'] eq '3'">selected="selected"</if>>                                            Snooze/Unsnooze                                            </option>                                            <option value="4" <if condition="$_GET['searchstatus'] eq '4'">selected="selected"</if>>                                            Menu Push                                            </option>                                            <option value="5" <if condition="$_GET['searchstatus'] eq '5'">selected="selected"</if>>                                            Activate/Disable                                            </option>                                            <option value="6" <if condition="$_GET['searchstatus'] eq '6'">selected="selected"</if>>                                            Registration                                            </option>                                        </select>                                        &nbsp;&nbsp;{pigcms{:L('_BACK_DATE_SELECT_')}：                                        <input type="text" class="form-control" name="begin_time" style="width:120px;" id="d4311" value="{pigcms{$_GET.begin_time}" onfocus="WdatePicker({readOnly:true,dateFmt:'yyyy-MM-dd',lang:'en'})"/>&nbsp;                                        <input type="text" class="form-control" name="end_time" style="width:120px;" id="d4311" value="{pigcms{$_GET.end_time}" onfocus="WdatePicker({readOnly:true,dateFmt:'yyyy-MM-dd',lang:'en'})"/>&nbsp;                                        &nbsp;<input type="submit" value="{pigcms{:L('_BACK_SEARCH_')}" class="form-control"/>                                    </div>                                </form>                            </div>                            <!-------------------------------- 工具条 -------------------------------------->                        </div>                        <table class="table table-striped table-bordered table-hover dataTables-example">                            <thead>                            <tr>                                <th>Store ID</th>                                <th>Store Name</th>                                <th>Type</th>                                <th>Action</th>                                <th>Time</th>                                <th>Source</th>                            </tr>                            </thead>                            <tbody>                            <if condition="is_array($list)">                                <volist name="list" id="vo">                                    <tr>                                        <td>{pigcms{$vo.storeId}</td>                                        <td>{pigcms{$vo.name}</td>                                        <td>{pigcms{$vo.type}</td>                                        <td>{pigcms{$vo.action}</td>                                        <td>{pigcms{$vo.time|date='Y-m-d H:i:s',###}</td>                                        <td>{pigcms{$vo['source'] == 1 ? "Deliverect" : "Tutti"}</td>                                    </tr>                                </volist>                                <tr><td class="textcenter pagebar" colspan="6">{pigcms{$pagebar}</td></tr>                                <else/>                                <tr><td class="textcenter red" colspan="6">{pigcms{:L('_BACK_EMPTY_')}</td></tr>                            </if>                            </tbody>                            <tfoot>                            <tr>                            </tr>                            </tfoot>                        </table>                    </div>                </div>            </div>        </div><include file="Public:footer"/>