<include file="Public:header"/>		<div class="mainbox">			<div id="nav" class="mainnav_title">				<ul>					<!--a href="{pigcms{:U('Merchant/store_auth')}">待审核列表</a>|					<a href="{pigcms{:U('Merchant/store_auth', array('status' => 1))}">已审核列表</a>| -->                    <a href="{pigcms{:U('Merchant/store_apply')}" class="on">{pigcms{:L('J_STORE_QUALIFICATION_CHECK')}</a>				</ul>			</div>			<form name="myform" id="myform" action="" method="post">				<div class="table-list">					<table width="100%" cellspacing="0">						<thead>							<tr>								<th>{pigcms{:L('G_ID')}</th>								<th>{pigcms{:L('E_STORE_NAME')}</th>								<th>{pigcms{:L('E_CONTACT_NUMBER1')}</th>								<th>{pigcms{:L('E_STORE_ADDRESS')}</th>								<th>{pigcms{:L('J_CONTACTS_NAME')}</th>								<th>{pigcms{:L('E_CONTACT_EMAIL')}</th>								<th>{pigcms{:L('J_APPLICATION_STARTED')}</th>							</tr>						</thead>						<tbody>							<if condition="is_array($store_list)">								<volist name="store_list" id="vo">									<tr>										<td>{pigcms{$vo.id}</td>										<td>{pigcms{$vo.store_name}</td>										<td>{pigcms{$vo.phone}</td>                                        <td>{pigcms{$vo.store_address}</td>                                        <td>{pigcms{$vo.first_name} {pigcms{$vo.last_name}</td>                                        <td>{pigcms{$vo.email}</td>                                        <td>{pigcms{$vo.create_time}</td>									</tr>								</volist>								<tr><td class="textcenter pagebar" colspan="11">{pigcms{$pagebar}</td></tr>							<else/>								<tr><td class="textcenter red" colspan="10">列表为空！</td></tr>							</if>						</tbody>					</table>				</div>			</form>		</div><include file="Public:footer"/>