<include file="Public:header"/><div id="wrapper">    <include file="Public:left_menu"/>    <!----------------------------------------    以上不要写代码     ------------------------------------------------>    <div class="row wrapper border-bottom white-bg page-heading">        <div class="col-lg-9">            <h2>{pigcms{:L('_BACK_AREA_LIST_')}</h2>            <ol class="breadcrumb">                <li class="breadcrumb-item">                    <a href="{pigcms{:U('Index/index')}">Home</a>                </li>                <!--                <li class="breadcrumb-item">-->                <!--                    <a>UI Elements</a>-->                <!--                </li>-->                <li class="breadcrumb-item active">                    <strong>{pigcms{:L('_BACK_AREA_LIST_')}</strong>                </li>            </ol>        </div>        <div class="col-lg-3" style="height 90px;margin-top:40px;">        </div>    </div>    <div class="wrapper wrapper-content animated fadeInRight">        <div class="row">            <div class="col-lg-12">                <div class="ibox ">                    <div class="ibox-title tutti_hidden_obj">                        <h5>{pigcms{:L('_BACK_AREA_LIST_')}</h5>                        <div class="ibox-tools">                        </div>                    </div>                    <div class="ibox-content">                        <div class="table-responsive">                            <!-------------------------------- 工具条 -------------------------------------->                            <div style="height: 60px;">                                <div id="tool_bar" class="form-inline tutti_toolbar">                                    <form action="{pigcms{:U('Shop/shop')}" method="get">                                        <input type="hidden" name="c" value="Shop"/>                                        <input type="hidden" name="a" value="shop"/>                                        <if condition="$system_session['level'] neq 3">                                            City:&nbsp;&nbsp;                                            <select name="city_id" id="city_id" class="form-control">                                                <option value="0"                                                <if condition="$city_id eq '' or $city_id eq 0">selected="selected"</if>                                                >All</option>                                                <volist name="city" id="vo">                                                    <option value="{pigcms{$vo.area_id}"                                                    <if condition="$city_id eq $vo['area_id']">selected="selected"</if>                                                    >{pigcms{$vo.area_name}</option>                                                </volist>                                            </select>                                        </if>&nbsp;&nbsp;                                        {pigcms{:L('_BACK_STORE_NAME_')}:&nbsp;                                        <input type="text" name="keyword" value="{pigcms{$_GET['keyword']}" class="form-control"/>                                        <input type="submit" value="{pigcms{:L('_BACK_SEARCH_')}" class="form-control"/>                                    </form>                                </div>                            </div>                        </div>                        <table class="table table-striped table-bordered table-hover dataTables-example">                            <thead>                            <tr>                                <th>{pigcms{:L('_BACK_MER_NAME_')}</th>                                <th>{pigcms{:L('_BACK_STORE_NAME_')}</th>                                <th>{pigcms{:L('_BACK_SERVICE_AREA_')}</th>                                <th>{pigcms{:L('_BACK_MER_PHONE_')}</th>                                <th>{pigcms{:L('_BACK_STORE_PHONE_')}</th>                                <th>{pigcms{:L('_BACK_SETUP_TIME_')}</th>                                <th class="textcenter">{pigcms{:L('_BACK_CZ_')}</th>                            </tr>                            </thead>                            <tbody>                                <if condition="is_array($order_list)">                                    <volist name="order_list" id="vo">                                        <tr>                                            <td>{pigcms{$vo.merchant_name}</td>                                            <td>{pigcms{$vo.store_name}</td>                                            <td><if condition="$vo['delivery_radius'] gt 0">{pigcms{$vo.delivery_radius|floatval} {pigcms{:L('_BACK_MILES_')}<else /><b style="color:red">{pigcms{:L('_BACK_UNSET_')}</b></if></td>                                            <td>{pigcms{$vo.merchant_phone}</td>                                            <td>{pigcms{$vo.store_phone}</td>                                            <td>{pigcms{$vo.create_time|date="Y-m-d H:i:s",###}</td>                                            <td class="textcenter"><a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Shop/shop_edit',array('store_id' => $vo['store_id']))}','{pigcms{:L(\'_BACK_EDIT_AREA_\')}',680,480,true,false,false,editbtn,'edit',true);"><button class="btn btn-white text-grey" type="button">{pigcms{:L('_BACK_STORE_SET_')}</button></a></td>                                        </tr>                                    </volist>                                    <tr><td class="textcenter pagebar" colspan="7">{pigcms{$pagebar}</td></tr>                                    <else/>                                    <tr><td class="textcenter red" colspan="7">{pigcms{:L('_BACK_EMPTY_')}</td></tr>                                </if>                            </tbody>                            <tfoot>                            <tr>                            </tr>                            </tfoot>                        </table>                    </div>                </div>            </div>        </div>    </div><include file="Public:footer"/>